import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Box, 
  Button, 
  Container, 
  Typography, 
  Paper, 
  Grid, 
  List, 
  ListItem, 
  ListItemText, 
  ListItemSecondaryAction, 
  IconButton, 
  Chip,
  CircularProgress,
  Alert
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import CloseIcon from '@mui/icons-material/Close';
import { useAuth } from '../contexts/AuthContext';
import { Blog, BlogFormData } from '../types';
import BlogForm from '../components/BlogForm';

// Import blog service functions with proper types
const getBlogs = async (): Promise<Blog[]> => {
  const response = await fetch('/api/blogs');
  if (!response.ok) throw new Error('Failed to fetch blogs');
  return response.json();
};

const createBlog = async (data: BlogFormData): Promise<Blog> => {
  const formData = new FormData();
  Object.entries(data).forEach(([key, value]) => {
    if (value !== undefined && value !== null) {
      formData.append(key, value instanceof File ? value : String(value));
    }
  });

  const response = await fetch('/api/blogs', {
    method: 'POST',
    body: formData,
  });
  if (!response.ok) throw new Error('Failed to create blog');
  return response.json();
};

const updateBlog = async (id: string, data: BlogFormData): Promise<Blog> => {
  const formData = new FormData();
  Object.entries(data).forEach(([key, value]) => {
    if (value !== undefined && value !== null) {
      formData.append(key, value instanceof File ? value : String(value));
    }
  });

  const response = await fetch(`/api/blogs/${id}`, {
    method: 'PUT',
    body: formData,
  });
  if (!response.ok) throw new Error('Failed to update blog');
  return response.json();
};

const deleteBlog = async (id: string): Promise<void> => {
  const response = await fetch(`/api/blogs/${id}`, {
    method: 'DELETE',
  });
  if (!response.ok) throw new Error('Failed to delete blog');
};

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const { user, logout } = useAuth();
  const [blogs, setBlogs] = useState<Blog[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [editingBlog, setEditingBlog] = useState<Blog | null>(null);
  const [showForm, setShowForm] = useState(false);
  const [formError, setFormError] = useState<string | null>(null);
  const [formLoading, setFormLoading] = useState(false);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const data = await getBlogs();
        // Sort blogs by creation date (newest first)
        const sortedBlogs = [...data].sort((a, b) => 
          new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        );
        setBlogs(sortedBlogs);
        setError(null);
      } catch (error) {
        console.error('Error fetching blogs:', error);
        setError('Failed to load blogs. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  const handleCreateBlog = () => {
    setEditingBlog(null);
    setShowForm(true);
    setFormError(null);
  };

  const handleEditBlog = (blog: Blog) => {
    setEditingBlog(blog);
    setShowForm(true);
    setFormError(null);
  };

  const handleFormSubmit = async (formData: FormData) => {
    try {
      setFormLoading(true);
      setFormError(null);

      if (editingBlog && editingBlog._id) {
        // Update existing blog
        const updatedBlog = await updateBlog(editingBlog._id, formData as unknown as BlogFormData);
        setBlogs(blogs.map(blog => 
          blog._id === editingBlog._id ? updatedBlog : blog
        ));
      } else {
        // Create new blog
        const newBlog = await createBlog(formData as unknown as BlogFormData);
        setBlogs([newBlog, ...blogs]);
      }

      setShowForm(false);
    } catch (error: any) {
      console.error('Error saving blog:', error);
      setFormError(error.message || 'Failed to save blog. Please try again.');
    } finally {
      setFormLoading(false);
    }
  };

  const handleDeleteBlog = async (id: string | undefined) => {
    if (!id) return;
    if (!window.confirm('Are you sure you want to delete this blog post?')) {
      return;
    }

    try {
      await deleteBlog(id);
      setBlogs(blogs.filter(blog => blog._id !== id));
    } catch (error) {
      console.error('Error deleting blog:', error);
      setError('Failed to delete blog. Please try again.');
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4" component="h1">
          Welcome, {user?.name || 'User'}!
        </Typography>
        <Box display="flex" gap={2}>
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={handleCreateBlog}
            disabled={formLoading}
          >
            Create New Blog
          </Button>
          <Button
            variant="outlined"
            onClick={() => navigate('/blogs')}
            disabled={formLoading}
          >
            View All Blogs
          </Button>
        </Box>
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3 }}>
            <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
              <Typography variant="h6">Your Blog Posts</Typography>
              <Button 
                variant="outlined" 
                onClick={() => navigate('/blogs')}
              >
                View All Blogs
              </Button>
            </Box>

            {showForm && (
              <Paper sx={{ p: 3, mb: 4, mt: 2 }}>
                <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                  <Typography variant="h6">
                    {editingBlog ? 'Edit Blog Post' : 'Create New Blog Post'}
                  </Typography>
                  <IconButton 
                    onClick={() => {
                      setShowForm(false);
                      setFormError(null);
                    }}
                    disabled={formLoading}
                  >
                    <CloseIcon />
                  </IconButton>
                </Box>

                {formError && (
                  <Alert severity="error" sx={{ mb: 2 }}>
                    {formError}
                  </Alert>
                )}

                <BlogForm
                  initialData={editingBlog || undefined}
                  onSubmit={handleFormSubmit}
                  onCancel={() => setShowForm(false)}
                  loading={formLoading}
                />
              </Paper>
            )}

            {loading ? (
              <Box display="flex" justifyContent="center" mt={4}>
                <CircularProgress />
              </Box>
            ) : error ? (
              <Typography color="error" align="center" mt={4}>
                {error}
              </Typography>
            ) : blogs.length === 0 ? (
              <Typography>No blog posts found. Create your first post!</Typography>
            ) : (
              <List>
                {blogs.map((blog) => (
                  <ListItem 
                    key={blog._id} 
                    divider
                    sx={{
                      '&:hover': {
                        backgroundColor: 'action.hover',
                      }
                    }}
                  >
                    <ListItemText
                      primary={
                        <Box display="flex" alignItems="center" gap={1}>
                          {blog.title}
                          {blog.featured && (
                            <Chip 
                              label="Featured" 
                              size="small" 
                              color="primary" 
                              variant="outlined" 
                            />
                          )}
                        </Box>
                      }
                      secondary={`Created: ${new Date(blog.createdAt).toLocaleDateString()}`}
                    />
                    <ListItemSecondaryAction>
                      <Box>
                        <IconButton 
                          edge="end" 
                          aria-label="edit" 
                          onClick={() => handleEditBlog(blog)}
                          disabled={formLoading}
                        >
                          <EditIcon />
                        </IconButton>
                        <IconButton 
                          edge="end" 
                          aria-label="delete" 
                          onClick={() => handleDeleteBlog(blog._id)}
                          disabled={formLoading}
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Box>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            )}
          </Paper>
        </Grid>

        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
              Quick Actions
            </Typography>
            <Box display="flex" flexDirection="column" gap={2} mt={2}>
              <Button 
                variant="outlined" 
                onClick={() => navigate('/blogs')}
                fullWidth
              >
                View All Blogs
              </Button>
              <Button 
                variant="outlined" 
                onClick={() => navigate('/profile')}
                fullWidth
              >
                Edit Profile
              </Button>
              <Button 
                variant="outlined" 
                color="error"
                onClick={logout}
                fullWidth
              >
                Logout
              </Button>
            </Box>
          </Paper>

          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Account Information
            </Typography>
            <Box mt={2}>
              <Typography variant="subtitle2" color="text.secondary">
                Email
              </Typography>
              <Typography variant="body1" gutterBottom>
                {user?.email || 'N/A'}
              </Typography>

              <Typography variant="subtitle2" color="text.secondary" mt={2}>
                Member Since
              </Typography>
              <Typography variant="body1">
                {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
              </Typography>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Dashboard;
