import React from 'react';
import { Box, Typography, useTheme, useMediaQuery, IconButton } from '@mui/material';
import { KeyboardArrowLeft, KeyboardArrowRight } from '@mui/icons-material';
import { Blog } from '../types';
import BlogCard from './BlogCard';

interface BlogCarouselProps {
  blogs: (Blog & { _id?: string })[];
  onEdit: (blog: Blog & { _id?: string }) => void;
  onDelete: (blogId: string) => Promise<void>;
}

const BlogCarousel: React.FC<BlogCarouselProps> = ({ blogs, onEdit, onDelete }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const [currentIndex, setCurrentIndex] = React.useState(0);
  const itemsPerPage = isMobile ? 1 : 3;

  const handleNext = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex >= blogs.length - itemsPerPage ? 0 : prevIndex + 1
    );
  };

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? blogs.length - itemsPerPage : prevIndex - 1
    );
  };

  if (!blogs.length) {
    return (
      <Box textAlign="center" py={4}>
        <Typography variant="h6" color="textSecondary">
          No blog posts available
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ position: 'relative', width: '100%', overflow: 'hidden' }}>
      <Box 
        sx={{
          display: 'flex',
          transition: 'transform 0.3s ease',
          transform: `translateX(-${currentIndex * (100 / itemsPerPage)}%)`,
          gap: 2,
          px: 2
        }}
      >
        {blogs.map((blog) => (
          <Box 
            key={blog._id || blog.title} 
            sx={{ 
              minWidth: `calc(${100 / itemsPerPage}% - ${theme.spacing(2)})`,
              flexShrink: 0,
              px: 1
            }}
          >
            <BlogCard 
              blog={blog} 
              onEdit={() => onEdit(blog)}
              onDelete={blog._id ? () => onDelete(blog._id) : undefined}
            />
          </Box>
        ))}
      </Box>

      {blogs.length > itemsPerPage && (
        <>
          <IconButton
            onClick={handlePrevious}
            sx={{
              position: 'absolute',
              left: 0,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
              },
              zIndex: 1,
              boxShadow: 1,
            }}
          >
            <KeyboardArrowLeft />
          </IconButton>
          <IconButton
            onClick={handleNext}
            sx={{
              position: 'absolute',
              right: 0,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
              },
              zIndex: 1,
              boxShadow: 1,
            }}
          >
            <KeyboardArrowRight />
          </IconButton>
        </>
      )}
    </Box>
  );
};

export default BlogCarousel;
