import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Typography,
  Box,
  useMediaQuery,
  useTheme,
  Button,
  Avatar,
  Divider,
  Fade
} from '@mui/material';
import { 
  Close as CloseIcon, 
  Edit as EditIcon, 
  Delete as DeleteIcon,
  OpenInNew as OpenInNewIcon,
  CalendarToday as CalendarIcon,
  Update as UpdateIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { format } from 'date-fns';

interface BlogImage {
  data: string;
  contentType: string;
  filename?: string;
}

interface Blog {
  id: string;
  _id?: string; // For MongoDB compatibility
  title: string;
  googleDriveLink: string;
  image: string | BlogImage;
  createdAt: string;
  updatedAt?: string;
  author?: {
    id: string;
    name: string;
    email?: string;
  };
}

interface BlogDetailProps {
  blog: Blog;
  open: boolean;
  onClose: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const BlogDetail: React.FC<BlogDetailProps> = ({ blog, open, onClose, onEdit, onDelete }) => {
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));
  const [loading, setLoading] = useState(true);

  // Format date for display
  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMMM d, yyyy');
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  // Get Google Docs embed URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      // Extract document ID using a more robust method
      const match = url.match(/[\w-]{25,}/);
      if (match && match[0]) {
        const docId = match[0];
        return `https://docs.google.com/document/d/${docId}/preview?rm=minimal&embedded=true&single=true&widget=false&headers=false&chrome=false`;
      }
      return url;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return url;
    }
  };

  // Handle iframe load
  const handleIframeLoad = () => {
    setLoading(false);
    // Force a small delay to ensure the iframe is fully rendered
    setTimeout(() => {
      const iframe = document.querySelector('iframe');
      if (iframe) {
        iframe.style.opacity = '1';
        iframe.style.transition = 'opacity 0.3s ease-in-out';
      }
    }, 100);
  };

  return (
    <Dialog
      fullScreen={fullScreen}
      open={open}
      onClose={onClose}
      maxWidth="xl"
      fullWidth
      PaperProps={{
        sx: {
          height: '95vh',
          maxHeight: 'none',
          borderRadius: 3,
          overflow: 'hidden',
          background: theme.palette.background.default,
          boxShadow: theme.shadows[10],
          '& .MuiDialog-container': {
            backdropFilter: 'blur(4px)',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            padding: fullScreen ? 0 : 2,
          },
          [theme.breakpoints.up('md')]: {
            height: '90vh',
            maxHeight: '1200px',
          }
        },
      }}
      transitionDuration={300}
      TransitionComponent={Fade}
    >
      <DialogTitle sx={{ 
        background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.primary.dark} 100%)`,
        color: theme.palette.primary.contrastText,
        py: 2,
        px: 3,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        boxShadow: theme.shadows[2],
        position: 'relative',
        '&::after': {
          content: '""',
          position: 'absolute',
          bottom: 0,
        }
      }}>
        {blog.title}
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            color: theme.palette.primary.contrastText,
            '&:hover': {
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
            },
            transition: 'all 0.2s ease',
            transform: 'scale(0.9)'
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers sx={{ 
        p: 0, 
        display: 'flex', 
        flexDirection: 'column',
        flex: 1,
        backgroundColor: theme.palette.background.default,
        overflow: 'hidden',
        '&.MuiDialogContent-root': {
          padding: 0,
        }
      }}>
        {/* Blog Header */}
        <Box sx={{ 
          p: 3, 
          pb: 2,
          backgroundColor: theme.palette.background.paper,
          borderBottom: `1px solid ${theme.palette.divider}`
        }}>
          <Typography variant="h4" component="h1" sx={{ 
            fontWeight: 700,
            mb: 2,
            lineHeight: 1.3,
            color: theme.palette.text.primary,
          }}>
            {blog.title}
          </Typography>

          {/* Author and Date */}
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Avatar sx={{ 
              width: 32, 
              height: 32, 
              mr: 1.5,
              backgroundColor: theme.palette.primary.main,
              color: theme.palette.primary.contrastText,
              fontSize: '0.875rem',
              fontWeight: 500
            }}>
              {blog.author?.charAt(0) || <PersonIcon fontSize="small" />}
            </Avatar>

            <Box sx={{ flex: 1 }}>
              <Typography variant="subtitle2" sx={{ 
                fontWeight: 500,
                color: theme.palette.text.primary,
                lineHeight: 1.2
              }}>
                {blog.author || 'Unknown Author'}
              </Typography>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5, mt: 0.5 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', color: theme.palette.text.secondary }}>
                  <CalendarIcon sx={{ fontSize: '1rem', mr: 0.5 }} />
                  <Typography variant="caption" sx={{ fontSize: '0.75rem' }}>
                    {formatDate(blog.createdAt)}
                  </Typography>
                </Box>

                {blog.updatedAt !== blog.createdAt && (
                  <Box sx={{ display: 'flex', alignItems: 'center', color: theme.palette.text.secondary }}>
                    <UpdateIcon sx={{ fontSize: '1rem', mr: 0.5 }} />
                    <Typography variant="caption" sx={{ fontSize: '0.75rem' }}>
                      Updated {formatDate(blog.updatedAt)}
                    </Typography>
                  </Box>
                )}
              </Box>
            </Box>
          </Box>

          <Divider sx={{ mt: 2, mb: 1 }} />
        </Box>

        {/* Blog Content */}
        <Box sx={{ 
          flex: 1, 
          position: 'relative', 
          overflow: 'auto',
          display: 'flex',
          flexDirection: 'column',
          backgroundColor: theme.palette.background.paper,
          p: 0,
          m: 0,
          '& iframe': {
            border: 'none',
            width: '100%',
            height: '100%',
            minHeight: 'calc(100vh - 200px)',
            backgroundColor: theme.palette.background.paper,
            '&.MuiBox-root': {
              borderRadius: 0,
              p: 0,
              m: 0,
            },
            '& body': {
              backgroundColor: 'transparent !important',
              padding: '40px 0 !important',
              '& .kix-page-paginated': {
                boxShadow: 'none !important',
                backgroundColor: 'transparent !important',
              },
              '& .kix-page': {
                backgroundColor: 'transparent !important',
                boxShadow: 'none !important',
              },
              '& .kix-page-content': {
                padding: '60px 80px !important',
                maxWidth: '1000px',
                margin: '0 auto !important',
                backgroundColor: `${theme.palette.background.paper} !important`,
                boxShadow: 'none !important',
                '& p, & div, & span, & li, & a': {
                  fontFamily: `${theme.typography.fontFamily} !important`,
                  lineHeight: '1.8 !important',
                  fontSize: '1.1rem !important',
                  color: `${theme.palette.text.primary} !important`,
                },
                '& h1, & h2, & h3, & h4, & h5, & h6': {
                  fontFamily: `${theme.typography.fontFamily} !important`,
                  color: `${theme.palette.text.primary} !important`,
                  margin: '1.5em 0 0.8em 0 !important',
                },
                '& p': {
                  marginBottom: '1.8em !important',
                },
                '& ul, & ol': {
                  margin: '1.5em 0 !important',
                  paddingLeft: '2em !important',
                  '& li': {
                    marginBottom: '0.8em !important',
                  }
                }
              }
            }
          }
        }}>
          {!loading && (
            <Box
              component="iframe"
              src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
              title={blog.title}
              width="100%"
              height="100%"
              frameBorder="0"
              onLoad={handleIframeLoad}
              style={{
                flex: 1,
                minHeight: 'calc(100vh - 300px)',
                opacity: 0,
                transition: 'opacity 0.3s ease-in-out',
                border: 'none',
                backgroundColor: theme.palette.background.paper,
              }}
              allowFullScreen
            />
          )}
        </Box>
      </DialogContent>

      <DialogActions sx={{ 
        px: 3, 
        py: 2, 
        borderTop: `1px solid ${theme.palette.divider}`,
        justifyContent: 'space-between',
        backgroundColor: theme.palette.background.paper,
        '&.MuiDialogActions-root': {
          padding: theme.spacing(2, 3),
        }
      }}>
        <Box sx={{ display: 'flex', gap: 1.5 }}>
          {onEdit && (
            <Button 
              onClick={onEdit} 
              variant="outlined"
              color="primary"
              size="medium"
              startIcon={<EditIcon />}
              sx={{
                textTransform: 'none',
                borderRadius: '8px',
                px: 2,
                py: 0.8,
                fontWeight: 500,
                transition: 'all 0.2s ease',
                '&:hover': {
                  backgroundColor: theme.palette.primary.light + '10',
                  transform: 'translateY(-1px)',
                }
              }}
            >
              Edit Post
            </Button>
          )}
          {onDelete && (
            <Button 
              onClick={onDelete} 
              variant="outlined"
              color="error"
              size="medium"
              startIcon={<DeleteIcon />}
              sx={{
                textTransform: 'none',
                borderRadius: '8px',
                px: 2,
                py: 0.8,
                fontWeight: 500,
                transition: 'all 0.2s ease',
                '&:hover': {
                  backgroundColor: theme.palette.error.light + '10',
                  transform: 'translateY(-1px)',
                }
              }}
            >
              Delete
            </Button>
          )}
        </Box>
        <Button 
          variant="contained" 
          color="primary" 
          endIcon={<OpenInNewIcon />}
          href={blog.googleDriveLink} 
          target="_blank"
          rel="noopener noreferrer"
          size="medium"
          sx={{
            textTransform: 'none',
            borderRadius: '8px',
            px: 2.5,
            py: 0.9,
            fontWeight: 500,
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'all 0.2s ease',
            '&:hover': {
              transform: 'translateY(-1px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
            }
          }}
        >
          Open in Google Docs
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default BlogDetail;
