import React, { useState, useEffect, useCallback } from 'react';
import { 
  Box, 
  IconButton, 
  Typography, 
  useTheme, 
  Card, 
  CardMedia, 
  Button, 
  Dialog, 
  DialogActions, 
  DialogContent, 
  DialogTitle 
} from '@mui/material';
import { ChevronLeft, ChevronRight, ArrowForward } from '@mui/icons-material';
import { Blog } from '../types/blog';
import { format } from 'date-fns';

interface BlogCarouselProps {
  blogs: (Blog & { _id?: string })[];
  onEdit?: (blog: Blog & { _id?: string }) => void;
  onDelete?: (blogId: string) => Promise<void>;
  onCloseDetail?: () => void;
}

const BlogCarousel: React.FC<BlogCarouselProps> = ({ 
  blogs, 
  onEdit, 
  onDelete,
  onCloseDetail 
}) => {
  const theme = useTheme();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [openDetail, setOpenDetail] = useState<{open: boolean, blog: (Blog & { _id?: string }) | null}>({open: false, blog: null});

  // Auto-advance carousel
  useEffect(() => {
    if (blogs.length <= 1 || isPaused) return;

    const timer = setInterval(() => {
      setCurrentIndex(prevIndex => (prevIndex + 1) % blogs.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [blogs.length, isPaused]);

  const goToNext = useCallback((): void => {
    setCurrentIndex((prevIndex: number) => (prevIndex + 1) % blogs.length);
  }, [blogs.length]);

  const goToPrev = useCallback((): void => {
    setCurrentIndex((prevIndex: number) => (prevIndex - 1 + blogs.length) % blogs.length);
  }, [blogs.length]);

  const handleOpenDetail = (blog: Blog & { _id?: string }) => {
    setOpenDetail({ open: true, blog });
  };

  const handleCloseDetail = useCallback(() => {
    setOpenDetail({ open: false, blog: null });
    if (onCloseDetail) onCloseDetail();
  }, [onCloseDetail]);

  if (!blogs || blogs.length === 0) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography>No blog posts available</Typography>
      </Box>
    );
  }

  const currentBlog = blogs[currentIndex];
  const hasMultipleItems = blogs.length > 1;

  return (
    <Box 
      sx={{ 
        position: 'relative',
        width: '100%',
        maxWidth: '1400px',
        mx: 'auto',
        px: { xs: 2, sm: 3, md: 4 },
        mb: 6,
        '&:hover .carousel-arrow': {
          opacity: 1,
        },
      }}
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <Box
        sx={{
          position: 'relative',
          width: '100%',
          overflow: 'hidden',
          borderRadius: 2,
          boxShadow: 3,
        }}
      >
        {hasMultipleItems && (
          <>
            <IconButton
              className="carousel-arrow"
              onClick={goToPrev}
              sx={{
                position: 'absolute',
                left: 16,
                top: '50%',
                transform: 'translateY(-50%)',
                zIndex: 2,
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                '&:hover': { backgroundColor: 'rgba(255, 255, 255, 0.95)' },
                opacity: 0.8,
                transition: 'opacity 0.3s ease',
              }}
            >
              <ChevronLeft />
            </IconButton>
            <IconButton
              className="carousel-arrow"
              onClick={goToNext}
              sx={{
                position: 'absolute',
                right: 16,
                top: '50%',
                transform: 'translateY(-50%)',
                zIndex: 2,
                backgroundColor: 'rgba(255, 255, 255, 0.8)',
                '&:hover': { backgroundColor: 'rgba(255, 255, 255, 0.95)' },
                opacity: 0.8,
                transition: 'opacity 0.3s ease',
              }}
            >
              <ChevronRight />
            </IconButton>
          </>
        )}

        <Box sx={{ display: 'flex', transition: 'transform 0.5s ease', height: '100%' }}>
          <Card
            key={currentBlog._id || 'default'}
            sx={{
              flex: '0 0 100%',
              position: 'relative',
              borderRadius: 2,
              overflow: 'hidden',
              height: '100%',
              display: 'flex',
              flexDirection: 'column',
            }}
          >
            <Box sx={{ position: 'relative', width: '100%', paddingTop: '56.25%' }}>
              <CardMedia
                component="img"
                image={currentBlog.imageUrl || '/placeholder.jpg'}
                alt={currentBlog.title}
                sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                }}
              />
              <Box
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)',
                  color: 'white',
                  p: 3,
                }}
              >
                <Typography variant="h5" component="h2" sx={{ mb: 1, fontWeight: 'bold' }}>
                  {currentBlog.title}
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleOpenDetail(currentBlog)}
                  endIcon={<ArrowForward />}
                  sx={{
                    marginTop: 1,
                    backgroundColor: theme.palette.primary.main,
                    '&:hover': { 
                      backgroundColor: theme.palette.primary.dark 
                    },
                  }}
                >
                  See More
                </Button>
              </Box>
            </Box>
          </Card>
        </Box>
      </Box>

      {hasMultipleItems && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2, gap: 1 }}>
          {blogs.map((_, index) => (
            <Box
              key={index}
              onClick={() => setCurrentIndex(index)}
              sx={{
                width: 10,
                height: 10,
                borderRadius: '50%',
                bgcolor: currentIndex === index ? 'primary.main' : 'action.disabled',
                cursor: 'pointer',
                transition: 'background-color 0.3s',
                '&:hover': { bgcolor: 'primary.dark' },
              }}
            />
          ))}
        </Box>
      )}

      {openDetail.open && openDetail.blog && (
        <Dialog 
          open={openDetail.open} 
          onClose={handleCloseDetail} 
          maxWidth="md" 
          fullWidth
        >
          <DialogTitle>{openDetail.blog.title}</DialogTitle>
          <DialogContent>
            <Box sx={{ marginBottom: 3 }}>
              <img
                src={openDetail.blog.imageUrl || '/placeholder.jpg'}
                alt={openDetail.blog.title}
                style={{ 
                  width: '100%', 
                  borderRadius: '8px', 
                  marginBottom: '16px' 
                }}
              />
              <Typography variant="body1" sx={{ whiteSpace: 'pre-line' }}>
                {openDetail.blog.content}
              </Typography>
            </Box>
            <Box sx={{ 
              marginTop: 2, 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center' 
            }}>
              <Typography variant="caption" color="textSecondary">
                {openDetail.blog.createdAt && 
                  format(new Date(openDetail.blog.createdAt), 'MMMM d, yyyy')}
              </Typography>
              {onEdit && onDelete && openDetail.blog._id && (
                <Box>
                  <Button
                    onClick={() => {
                      onEdit(openDetail.blog!);
                      handleCloseDetail();
                    }}
                    color="primary"
                    size="small"
                  >
                    Edit
                  </Button>
                  <Button
                    onClick={async () => {
                      if (onDelete && openDetail.blog?._id) {
                        await onDelete(openDetail.blog._id);
                        handleCloseDetail();
                      }
                    }}
                    color="error"
                    size="small"
                    sx={{ marginLeft: 1 }}
                  >
                    Delete
                  </Button>
                </Box>
              )}
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleCloseDetail} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </Box>
  );
};

export default BlogCarousel;
