import React, { useState, useEffect } from 'react';
import {
  TextField,
  Button,
  Box,
  CircularProgress,
  Typography,
} from '@mui/material';
import { Image as ImageIcon } from '@mui/icons-material';
import { BlogFormData } from '../types';
import { isValidGoogleDocsUrl, isValidImageUrl } from '../utils/validation';

interface BlogFormProps {
  initialData?: BlogFormData;
  onSubmit: (data: BlogFormData) => Promise<void> | void;
  onCancel: () => void;
  loading?: boolean;
}

// Custom hook for handling image URLs with fallbacks
const useImageWithFallback = (url: string) => {
  const [imageUrl, setImageUrl] = useState<string>('');
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (!url) {
      setImageUrl('');
      setError(null);
      return;
    }

    let isMounted = true;
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout

    const tryUrl = async (testUrl: string, isFallback = false) => {
      if (!isMounted) return;

      try {
        setIsLoading(true);
        setError(null);

        // First, try a HEAD request to check if the URL is accessible
        const response = await fetch(testUrl, {
          method: 'HEAD',
          signal: controller.signal,
          mode: 'no-cors', // This is just to check if the URL is reachable
        });

        // If we get here, the URL is accessible
        if (isMounted) {
          setImageUrl(testUrl);
          setError(null);
        }
      } catch (err) {
        if (!isMounted) return;

        if (!isFallback) {
          // Try with a CORS proxy as fallback
          const proxyUrl = `https://corsproxy.io/?${encodeURIComponent(testUrl)}`;
          await tryUrl(proxyUrl, true);
        } else {
          setError('Could not load image. The URL may be invalid or the server may be blocking access.');
          setImageUrl('');
        }
      } finally {
        if (isMounted) {
          setIsLoading(false);
        }
      }
    };

    tryUrl(url);

    return () => {
      isMounted = false;
      clearTimeout(timeoutId);
      controller.abort();
    };
  }, [url]);

  return { imageUrl, error, isLoading };
};

const BlogForm: React.FC<BlogFormProps> = ({
  initialData,
  onSubmit,
  onCancel,
  loading = false,
}) => {
  const [formData, setFormData] = useState<BlogFormData>({
    title: '',
    googleDriveLink: '',
    image: '',
  });
  const { imageUrl: imagePreview, error: imageError, isLoading: imageLoading } = useImageWithFallback(formData.image);
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Initialize form with initialData if provided
  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
    }
  }, [initialData]);

  // Update errors when image error changes
  useEffect(() => {
    if (imageError && formData.image) {
      setErrors(prev => ({ ...prev, image: imageError }));
    } else if (errors.image) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors.image;
        return newErrors;
      });
    }
  }, [imageError, formData.image]);

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.googleDriveLink) {
      newErrors.googleDriveLink = 'Google Drive link is required';
    } else if (!isValidGoogleDocsUrl(formData.googleDriveLink)) {
      newErrors.googleDriveLink = 'Please enter a valid Google Docs URL';
    }

    // Image is optional, but if provided, it should be a valid URL
    if (formData.image && !isValidImageUrl(formData.image)) {
      newErrors.image = 'Please enter a valid image URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for the field being edited
    if (errors[name as keyof typeof errors]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name as keyof typeof newErrors];
        return newErrors;
      });
    }
  };

  const handleImageUrlChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const url = e.target.value.trim();
    setFormData(prev => ({
      ...prev,
      image: url
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSubmit(formData);
    } catch (error) {
      console.error('Error submitting form:', error);
      setErrors(prev => ({ ...prev, form: 'Failed to submit form. Please try again.' }));
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      <TextField
        margin="normal"
        required
        fullWidth
        id="title"
        label="Blog Title"
        name="title"
        autoComplete="off"
        autoFocus
        value={formData.title}
        onChange={handleChange}
        error={!!errors.title}
        helperText={errors.title}
        disabled={loading}
      />

      <TextField
        margin="normal"
        required
        fullWidth
        name="googleDriveLink"
        label="Google Drive Document Link"
        type="url"
        id="googleDriveLink"
        autoComplete="off"
        value={formData.googleDriveLink}
        onChange={handleChange}
        error={!!errors.googleDriveLink}
        helperText={
          errors.googleDriveLink ||
          'Make sure the document is shared with "Anyone with the link"'
        }
        disabled={loading}
      />

      <Box mt={2} mb={2}>
        <Typography variant="subtitle2" gutterBottom>
          Image URL (Optional)
        </Typography>
        <Box mb={3}>
          <TextField
            fullWidth
            label="Image URL"
            variant="outlined"
            name="image"
            value={formData.image}
            onChange={handleImageUrlChange}
            error={!!errors.image}
            helperText={errors.image || 'Paste the URL of your image (e.g., from Google Drive, Imgur, etc.)'}
            margin="normal"
            disabled={loading}
            InputProps={{
              startAdornment: <ImageIcon color="action" sx={{ mr: 1, color: 'text.secondary' }} />,
            }}
          />
          {imagePreview && (
            <Box mt={2} textAlign="center">
              <Typography variant="caption" display="block" gutterBottom>
                Image Preview:
              </Typography>
              <Box 
                sx={{
                  display: 'inline-block',
                  maxWidth: '100%',
                  maxHeight: '300px',
                  border: '1px solid #eee',
                  borderRadius: '4px',
                  overflow: 'hidden',
                  p: 1,
                  backgroundColor: '#f9f9f9',
                  position: 'relative'
                }}
              >
                <img
                  src={imagePreview}
                  alt="Preview"
                  style={{
                    maxWidth: '100%',
                    maxHeight: '280px',
                    objectFit: 'contain',
                    display: 'block',
                    opacity: imagePreview ? 1 : 0,
                    transition: 'opacity 0.3s ease-in-out'
                  }}
                  onLoad={(e) => {
                    // Clear any previous errors when image loads successfully
                    if (errors.image) {
                      setErrors(prev => {
                        const newErrors = { ...prev };
                        delete newErrors.image;
                        return newErrors;
                      });
                    }
                  }}
                />
                <Box 
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    bgcolor: 'rgba(0,0,0,0.5)',
                    borderRadius: '50%',
                    width: 24,
                    height: 24,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    '&:hover': {
                      bgcolor: 'rgba(0,0,0,0.7)',
                    }
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    setFormData(prev => ({ ...prev, image: '' }));
                  }}
                >
                  <Typography color="white" variant="body2" sx={{ lineHeight: 1 }}>×</Typography>
                </Box>
              </Box>
            </Box>
          )}
        </Box>
      </Box>

      <Box mt={3} display="flex" justifyContent="flex-end" gap={2}>
        <Button
          onClick={onCancel}
          variant="outlined"
          disabled={loading}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={loading}
          startIcon={loading ? <CircularProgress size={20} /> : null}
        >
          {initialData ? 'Update Post' : 'Create Post'}
        </Button>
      </Box>
    </Box>
  );
};

export default BlogForm;
