import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  IconButton,
  useTheme,
  useMediaQuery,
  CircularProgress,
  Divider,
  Chip,
  Avatar
} from '@mui/material';
import { Close as CloseIcon, OpenInNew as OpenInNewIcon } from '@mui/icons-material';
import { format } from 'date-fns';
import { Blog } from '../types';

interface BlogDetailProps {
  blog: Blog;
  open: boolean;
  onClose: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const BlogDetail: React.FC<BlogDetailProps> = ({ blog, open, onClose, onEdit, onDelete }) => {
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));
  const [loading, setLoading] = useState(true);
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Format date for display
  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMMM d, yyyy');
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  // Get Google Docs embed URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      const urlObj = new URL(url);
      const pathParts = urlObj.pathname.split('/').filter(Boolean);
      let docId = pathParts[pathParts.length - 1];
      
      // Handle different Google Docs URL formats
      if (docId === 'edit') {
        docId = pathParts[pathParts.length - 2];
      }
      
      if (!docId) return '';
      return `https://docs.google.com/document/d/${docId}/preview`;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return '';
    }
  };

  // Handle image loading
  useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      setLoading(false);
      return;
    }

    const loadImage = async () => {
      try {
        setLoading(true);
        
        if (typeof blog.image === 'string') {
          if (blog.image.startsWith('data:image/')) {
            setImageSrc(blog.image);
          } else if (blog.image.startsWith('http')) {
            // Fallback for external URLs
            setImageSrc(blog.image);
          } else {
            // Handle base64 string without data URL prefix
            setImageSrc(`data:image/jpeg;base64,${blog.image}`);
          }
        } else if (blog.image && typeof blog.image === 'object' && 'data' in blog.image) {
          // Handle image object with data and contentType
          const base64Data = blog.image.data;
          const mimeType = blog.image.contentType || 'image/jpeg';
          
          if (typeof base64Data === 'string' && base64Data.startsWith('data:image/')) {
            setImageSrc(base64Data);
          } else {
            setImageSrc(`data:${mimeType};base64,${base64Data}`);
          }
        }
      } catch (error) {
        console.error('Error loading image:', error);
        setImageError(true);
      } finally {
        setLoading(false);
      }
    };

    loadImage();
  }, [blog.image]);

  // Handle iframe load
  const handleIframeLoad = () => {
    setLoading(false);
  };

  return (
    <Dialog
      fullScreen={fullScreen}
      open={open}
      onClose={onClose}
      maxWidth="lg"
      fullWidth
      PaperProps={{
        sx: {
          height: '90vh',
          maxHeight: '900px',
          borderRadius: 2,
          overflow: 'hidden',
        },
      }}
    >
      <DialogTitle sx={{ 
        backgroundColor: theme.palette.primary.main,
        color: theme.palette.primary.contrastText,
        py: 1.5,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}>
        <Typography variant="h6" component="div" noWrap sx={{ flex: 1, mr: 2 }}>
          {blog.title}
        </Typography>
        <IconButton edge="end" onClick={onClose} color="inherit">
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      
      <DialogContent dividers sx={{ p: 0, display: 'flex', flexDirection: 'column', flex: 1 }}>
        {/* Blog Metadata */}
        <Box sx={{ p: 3, pb: 1 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            {blog.author && (
              <>
                <Avatar 
                  sx={{ 
                    width: 32, 
                    height: 32, 
                    mr: 1.5,
                    backgroundColor: theme.palette.primary.main,
                    color: theme.palette.primary.contrastText,
                    fontSize: '0.875rem'
                  }}
                >
                  {blog.author.name ? blog.author.name.charAt(0).toUpperCase() : 'U'}
                </Avatar>
                <Typography variant="subtitle2" color="text.secondary">
                  {blog.author.name || 'Unknown Author'}
                </Typography>
                <Divider orientation="vertical" flexItem sx={{ mx: 1.5 }} />
              </>
            )}
            <Typography variant="caption" color="text.secondary">
              {formatDate(blog.createdAt)}
            </Typography>
            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <>
                <Divider orientation="vertical" flexItem sx={{ mx: 1.5 }} />
                <Typography variant="caption" color="text.secondary">
                  Updated: {formatDate(blog.updatedAt)}
                </Typography>
              </>
            )}
          </Box>
        </Box>

        {/* Featured Image */}
        {!loading && imageSrc && !imageError && (
          <Box sx={{ 
            width: '100%', 
            maxHeight: '300px',
            overflow: 'hidden',
            backgroundColor: theme.palette.grey[100],
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            borderTop: `1px solid ${theme.palette.divider}`,
            borderBottom: `1px solid ${theme.palette.divider}`,
          }}>
            <img
              src={imageSrc}
              alt={blog.title}
              style={{
                maxWidth: '100%',
                maxHeight: '300px',
                objectFit: 'contain',
                display: 'block',
              }}
              onError={() => setImageError(true)}
            />
          </Box>
        )}

        {/* Loading State */}
        {loading && (
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            minHeight: 200,
            backgroundColor: theme.palette.grey[50],
          }}>
            <CircularProgress />
          </Box>
        )}

        {/* Blog Content */}
        <Box sx={{ flex: 1, position: 'relative', overflow: 'hidden' }}>
          {!loading && (
            <Box
              component="iframe"
              src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
              title={blog.title}
              width="100%"
              height="100%"
              frameBorder="0"
              onLoad={handleIframeLoad}
              style={{
                border: 'none',
                minHeight: '400px',
                backgroundColor: theme.palette.background.paper,
              }}
              allowFullScreen
            />
          )}
        </Box>
      </DialogContent>
      
      <DialogActions sx={{ 
        px: 3, 
        py: 1.5, 
        borderTop: `1px solid ${theme.palette.divider}`,
        justifyContent: 'space-between'
      }}>
        <Box>
          {onEdit && (
            <Button 
              onClick={onEdit} 
              color="primary"
              size="small"
              sx={{ mr: 1 }}
            >
              Edit
            </Button>
          )}
          {onDelete && (
            <Button 
              onClick={onDelete} 
              color="error"
              size="small"
            >
              Delete
            </Button>
          )}
        </Box>
        <Button 
          variant="contained" 
          color="primary" 
          endIcon={<OpenInNewIcon />}
          href={blog.googleDriveLink} 
          target="_blank"
          rel="noopener noreferrer"
          size="small"
        >
          Open in Google Docs
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default BlogDetail;
