import React, { useState, useEffect } from 'react';
import {
  TextField,
  Button,
  Box,
  CircularProgress,
  TextFieldProps,
  Typography,
  IconButton,
  InputAdornment,
} from '@mui/material';
import { BlogFormData } from '../types';
import { isValidGoogleDocsUrl } from '../utils/validation.ts';

interface BlogFormProps {
  initialData?: BlogFormData;
  onSubmit: (data: BlogFormData) => void;
  onCancel: () => void;
  loading?: boolean;
}

const BlogForm: React.FC<BlogFormProps> = ({
  initialData,
  onSubmit,
  onCancel,
  loading = false,
}) => {
  const [formData, setFormData] = useState<BlogFormData>({
    title: '',
    googleDriveLink: '',
    image: '',
  });
  const [errors, setErrors] = useState<Record<string, string>>({});

  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
    }
  }, [initialData]);

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.googleDriveLink.trim()) {
      newErrors.googleDriveLink = 'Google Drive link is required';
    } else if (!isValidGoogleDocsUrl(formData.googleDriveLink)) {
      newErrors.googleDriveLink = 'Please enter a valid Google Docs URL';
    }

    if (!formData.image.trim()) {
      newErrors.image = 'Image URL is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange: TextFieldProps['onChange'] = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name as keyof typeof errors]) {
      setErrors((prev) => {
        const newErrors = { ...prev };
        delete newErrors[name as keyof typeof newErrors];
        return newErrors;
      });
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (validateForm()) {
      onSubmit(formData);
    }
  };

  const handlePreview = () => {
    if (formData.image) {
      window.open(formData.image, '_blank');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      <TextField
        margin="normal"
        required
        fullWidth
        id="title"
        label="Blog Title"
        name="title"
        autoComplete="off"
        autoFocus
        value={formData.title}
        onChange={handleChange}
        error={!!errors.title}
        helperText={errors.title}
        disabled={loading}
      />

      <TextField
        margin="normal"
        required
        fullWidth
        name="googleDriveLink"
        label="Google Drive Document Link"
        type="url"
        id="googleDriveLink"
        autoComplete="off"
        value={formData.googleDriveLink}
        onChange={handleChange}
        error={!!errors.googleDriveLink}
        helperText={
          errors.googleDriveLink || 
          'Make sure the document is shared with "Anyone with the link"'
        }
        disabled={loading}
      />

      <TextField
        margin="normal"
        required
        fullWidth
        name="image"
        label="Featured Image URL"
        type="url"
        id="image"
        autoComplete="off"
        value={formData.image}
        onChange={handleChange}
        error={!!errors.image}
        helperText={
          errors.image || (
            formData.image ? (
              <Typography 
                component="span" 
                variant="caption" 
                color="primary"
                sx={{ cursor: 'pointer' }}
                onClick={handlePreview}
              >
                Click to preview image
              </Typography>
            ) : null
          )
        }
        disabled={loading}
      />

      {formData.image && !errors.image && (
        <Box mt={2} mb={2} textAlign="center">
          <Typography variant="caption" display="block" gutterBottom>
            Image Preview:
          </Typography>
          <img 
            src={formData.image} 
            alt="Preview" 
            style={{ 
              maxWidth: '100%', 
              maxHeight: '200px',
              border: '1px solid #ddd',
              borderRadius: '4px',
            }} 
            onError={(e) => {
              const target = e.target as HTMLImageElement;
              target.style.display = 'none';
            }}
          />
        </Box>
      )}

      <Box mt={3} display="flex" justifyContent="flex-end" gap={2}>
        <Button
          onClick={onCancel}
          variant="outlined"
          disabled={loading}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={loading}
        >
          {loading ? (
            <CircularProgress size={24} />
          ) : initialData ? (
            'Update Post'
          ) : (
            'Create Post'
          )}
        </Button>
      </Box>
    </Box>
  );
};

export default BlogForm;
