import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  CircularProgress,
  FormControlLabel,
  Checkbox,
  Alert,
} from '@mui/material';
import { BlogFormData, BlogImage } from '../types';
import { getBlogById, updateBlog } from '../services/blogService';
import { useAuth } from '../contexts/AuthContext';

const BlogEditPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [currentImage, setCurrentImage] = useState<string | BlogImage | null>(null);

  // Using local state for form fields
  const [formData, setFormData] = useState<Omit<BlogFormData, 'id'>>({ 
    title: '',
    googleDriveLink: '',
    image: '',
    featured: false
  });

  // State for form reset after successful fetch
  const [initialData, setInitialData] = useState<Omit<BlogFormData, 'id'> | null>(null);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target as HTMLInputElement;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? (e.target as HTMLInputElement).checked : value
    }));
  };

  useEffect(() => {
    console.log('BlogEditPage mounted with id:', id);
    console.log('Current URL path:', window.location.pathname);

    const fetchBlog = async () => {
      if (!id) {
        const errorMsg = `No blog ID provided. Current path: ${window.location.pathname}`;
        console.error(errorMsg);
        setError('Invalid blog URL. Please check the link and try again.');
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        console.log('Fetching blog with ID:', id);

        // Log the full URL being called
        const apiUrl = `http://localhost:5000/api/blogs/${id}`;
        console.log('API URL:', apiUrl);

        const blog = await getBlogById(id);
        console.log('Fetched blog:', blog);

        if (!blog) {
          throw new Error(`Blog with ID ${id} not found`);
        }

        console.log('Current user ID:', user?.id);
        console.log('Blog author ID:', blog.author?.id);

        if (blog.author?.id !== user?.id) {
          console.error('Unauthorized access attempt:');
          console.error('- Current user ID:', user?.id);
          console.error('- Blog author ID:', blog.author?.id);
          throw new Error('You are not authorized to edit this blog');
        }

        const blogData = {
          title: blog.title,
          googleDriveLink: blog.googleDriveLink,
          featured: blog.featured || false,
          image: blog.image || ''
        };

        console.log('Setting form data:', blogData);
        setFormData(blogData);
        setInitialData(blogData);

        if (blog.image) {
          // Handle both string and BlogImage types for the image preview
          const imageUrl = typeof blog.image === 'string' 
            ? blog.image 
            : `data:${blog.image.contentType};base64,${blog.image.data}`;
          console.log('Setting image preview URL:', imageUrl?.substring(0, 50) + '...');
          setImagePreview(imageUrl);
        } else {
          console.log('No image found for blog');
        }
      } catch (err) {
        console.error('Error in fetchBlog:', err);
        const errorMessage = err instanceof Error ? err.message : 'Failed to load blog';
        setError(`${errorMessage}. Please try again or contact support if the problem persists.`);
      } finally {
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id, user]);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageUrl = reader.result as string;
        setImagePreview(imageUrl);
        setFormData(prev => ({
          ...prev,
          image: imageUrl
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!id) return;

    try {
      setLoading(true);
      setError(null);

      const data = new FormData();
      data.append('title', formData.title);
      data.append('googleDriveLink', formData.googleDriveLink);
      data.append('featured', String(formData.featured));

      // If there's a new image, add it to formData
      if (formData.image && formData.image !== initialData?.image) {
        // If it's a string (URL or base64), we need to fetch it as a blob
        if (typeof formData.image === 'string') {
          try {
            const response = await fetch(formData.image);
            const blob = await response.blob();
            data.append('image', blob);
          } catch (err) {
            console.error('Error processing image:', err);
            throw new Error('Failed to process image');
          }
        } else if (formData.image instanceof File) {
          data.append('image', formData.image);
        }
      }

      await updateBlog(id, data as unknown as Partial<BlogFormData>);
      navigate('/dashboard');
    } catch (err) {
      console.error('Error updating blog:', err);
      setError('Failed to update blog. Please try again.');
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={3}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  return (
    <Box p={3} maxWidth="md" mx="auto">
      <Typography variant="h4" component="h1" gutterBottom>
        Edit Blog Post
      </Typography>

      <Paper sx={{ p: 3 }} component="form" onSubmit={handleSubmit}>
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <TextField
          name="title"
          label="Title"
          value={formData.title}
          onChange={handleInputChange}
          fullWidth
          margin="normal"
          required
        />

        <TextField
          name="googleDriveLink"
          label="Google Drive Link"
          value={formData.googleDriveLink}
          onChange={handleInputChange}
          fullWidth
          margin="normal"
          required
          helperText="Paste the shareable link to your Google Doc"
        />

        <Box my={2}>
          <FormControlLabel
            control={
              <Checkbox
                name="featured"
                checked={formData.featured || false}
                onChange={handleInputChange}
              />
            }
            label="Featured Post"
          />
        </Box>

        <Box my={2}>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="blog-image-upload"
            type="file"
            onChange={handleImageChange}
          />
          <label htmlFor="blog-image-upload">
            <Button variant="outlined" component="span">
              {imagePreview ? 'Change Image' : 'Upload Image'}
            </Button>
          </label>

          {imagePreview && (
            <Box mt={2}>
              <img
                src={imagePreview}
                alt="Preview"
                style={{ maxWidth: '100%', maxHeight: '300px' }}
              />
            </Box>
          )}
        </Box>

        <Box mt={3} display="flex" gap={2}>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Changes'}
          </Button>

          <Button
            variant="outlined"
            onClick={() => navigate(-1)}
            disabled={loading}
          >
            Cancel
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default BlogEditPage;
