import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  useTheme,
  Box,
  IconButton
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Image as ImageIcon } from '@mui/icons-material';
import { Blog } from '../types';
import BlogDetail from './BlogDetail';

// Import the BlogImage type from types
import { BlogImage } from '../types';

// Remove local BlogImage interface since we're importing it

interface BlogCardProps {
  blog: Blog & { _id?: string }; // Make image required as per Blog type
  onEdit: () => void;
  onDelete: () => Promise<void>;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete }) => {
  const [openDetail, setOpenDetail] = useState(false);
  const theme = useTheme();
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Handle image source extraction
  React.useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      return;
    }

    if (typeof blog.image === 'string') {
      // Handle data URLs or external URLs
      if (blog.image.startsWith('data:image/')) {
        setImageSrc(blog.image);
      } else if (blog.image.startsWith('http')) {
        setImageSrc(blog.image);
      } else {
        // Handle base64 string without data URL prefix
        setImageSrc(`data:image/jpeg;base64,${blog.image}`);
      }
    } else if (blog.image && typeof blog.image === 'object' && 'data' in blog.image) {
      // Handle image object with data and contentType
      const base64Data = blog.image.data;
      const mimeType = blog.image.contentType || 'image/jpeg';

      if (typeof base64Data === 'string' && base64Data.startsWith('data:image/')) {
        setImageSrc(base64Data);
      } else {
        setImageSrc(`data:${mimeType};base64,${base64Data}`);
      }
    } else {
      setImageSrc('');
    }

    // Reset error state when image changes
    setImageError(false);
  }, [blog.image]);

  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  const handleDeleteClick = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await onDelete();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  return (
    <>
      <Card sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        transition: 'transform 0.2s, box-shadow 0.2s',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: theme.shadows[6],
        },
      }}>
        {/* Blog Image */}
        {imageSrc && !imageError ? (
          <Box sx={{ 
            position: 'relative', 
            width: '100%', 
            paddingTop: '56.25%',
            cursor: 'pointer',
          }} onClick={() => setOpenDetail(true)}>
            <img
              src={imageSrc}
              alt={blog.title}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                backgroundColor: theme.palette.grey[100],
                borderTopLeftRadius: theme.shape.borderRadius,
                borderTopRightRadius: theme.shape.borderRadius,
              }}
              onError={() => setImageError(true)}
            />
          </Box>
        ) : (
          <Box 
            sx={{
              backgroundColor: 'grey.200',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              paddingTop: '56.25%',
              position: 'relative',
              minHeight: 200,
              cursor: 'pointer',
              borderTopLeftRadius: theme.shape.borderRadius,
              borderTopRightRadius: theme.shape.borderRadius,
            }}
            onClick={() => setOpenDetail(true)}
          >
            <ImageIcon sx={{ fontSize: 48, color: 'grey.400' }} />
          </Box>
        )}

        {/* Blog Content */}
        <CardContent sx={{ flexGrow: 1, cursor: 'pointer' }} onClick={() => setOpenDetail(true)}>
          <Typography 
            gutterBottom 
            variant="h6" 
            component="h2" 
            noWrap
            sx={{
              fontWeight: 600,
              mb: 1.5,
              color: theme.palette.text.primary,
              '&:hover': {
                color: theme.palette.primary.main,
              },
            }}
          >
            {blog.title}
          </Typography>

          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Typography 
              variant="caption" 
              color="text.secondary"
              sx={{
                fontSize: '0.75rem',
                fontWeight: 500,
              }}
            >
              {formatDate(blog.createdAt)}
            </Typography>
            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <Typography 
                variant="caption" 
                color="text.secondary"
                sx={{
                  ml: 1,
                  fontSize: '0.7rem',
                  opacity: 0.8,
                }}
              >
                (Updated)
              </Typography>
            )}
          </Box>
        </CardContent>

        {/* Actions */}
        <CardActions sx={{ 
          mt: 'auto', 
          justifyContent: 'space-between',
          px: 2,
          pb: 2,
          pt: 0,
        }}>
          <Button 
            size="small" 
            color="primary"
            onClick={(e) => {
              e.stopPropagation();
              setOpenDetail(true);
            }}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
            }}
          >
            Read More
          </Button>

          <Box>
            <IconButton 
              size="small" 
              onClick={(e) => {
                e.stopPropagation();
                onEdit();
              }}
              sx={{
                color: theme.palette.primary.main,
                '&:hover': {
                  backgroundColor: theme.palette.primary.light + '22',
                },
              }}
            >
              <EditIcon fontSize="small" />
            </IconButton>

            <IconButton 
              size="small" 
              onClick={async (e) => {
                e.stopPropagation();
                await handleDeleteClick();
              }}
              sx={{
                color: theme.palette.error.main,
                '&:hover': {
                  backgroundColor: theme.palette.error.light + '22',
                },
              }}
            >
              <DeleteIcon fontSize="small" />
            </IconButton>
          </Box>
        </CardActions>
      </Card>

      {/* Blog Detail Dialog */}
      <BlogDetail
        blog={blog}
        open={openDetail}
        onClose={() => setOpenDetail(false)}
        onEdit={onEdit}
        onDelete={handleDeleteClick}
      />
    </>
  );
};

export default BlogCard;
