import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  CircularProgress,
  FormControlLabel,
  Checkbox,
  Alert,
} from '@mui/material';
import { useForm, Controller, FieldValues } from 'react-hook-form';
import { BlogFormData } from '../types';
import { getBlogById, updateBlog } from '../services/blogService';
import { useAuth } from '../contexts/AuthContext';

const BlogEditPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [currentImage, setCurrentImage] = useState<string | BlogImage | null>(null);

  const {
    control,
    handleSubmit,
    formState: { errors },
    reset,
    setValue,
    watch
  } = useForm<BlogFormData>();

  const imageValue = watch('image');

  useEffect(() => {
    const fetchBlog = async () => {
      if (!id) return;

      try {
        setLoading(true);
        const blog = await getBlogById(id);

        if (blog.author?.id !== user?.id) {
          throw new Error('You are not authorized to edit this blog');
        }

        reset({
          title: blog.title,
          googleDriveLink: blog.googleDriveLink,
          featured: blog.featured || false,
        });

        if (blog.image) {
          setCurrentImage(blog.image);
          // Handle both string and BlogImage types for the image preview
          const imageUrl = typeof blog.image === 'string' 
            ? blog.image 
            : `data:${blog.image.contentType};base64,${blog.image.data}`;
          setImagePreview(imageUrl);
        }
      } catch (err) {
        console.error('Error fetching blog:', err);
        setError('Failed to load blog. You may not have permission to edit it.');
      } finally {
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id, reset, user]);

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      const file = files[0];
      setValue('image', files);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const onSubmit = async (formData: FieldValues) => {
    if (!id) return;

    try {
      setLoading(true);
      setError(null);

      const data = new FormData();
      data.append('title', formData.title);
      data.append('googleDriveLink', formData.googleDriveLink);
      data.append('featured', String(!!formData.featured));

      // If there's a new image file, add it to formData
      if (formData.image && formData.image instanceof FileList && formData.image.length > 0) {
        data.append('image', formData.image[0]);
      }

      await updateBlog(id, data as unknown as Partial<BlogFormData>);
      navigate('/dashboard');
    } catch (err) {
      console.error('Error updating blog:', err);
      setError('Failed to update blog. Please try again.');
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={3}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  return (
    <Box p={3} maxWidth="md" mx="auto">
      <Typography variant="h4" component="h1" gutterBottom>
        Edit Blog Post
      </Typography>

      <Paper sx={{ p: 3 }} component="form" onSubmit={handleSubmit(onSubmit)}>
        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <Controller
          name="title"
          control={control}
          rules={{ required: 'Title is required' }}
          render={({ field }) => (
            <TextField
              {...field}
              label="Title"
              fullWidth
              margin="normal"
              error={!!errors.title}
              helperText={errors.title?.message}
            />
          )}
        />

        <Controller
          name="googleDriveLink"
          control={control}
          rules={{ required: 'Google Drive link is required' }}
          render={({ field }) => (
            <TextField
              {...field}
              label="Google Drive Link"
              fullWidth
              margin="normal"
              error={!!errors.googleDriveLink}
              helperText={
                errors.googleDriveLink?.message ||
                'Paste the shareable link to your Google Doc'
              }
            />
          )}
        />

        <Box my={2}>
<Controller
            name="featured"
            control={control}
            defaultValue={false}
            render={({ field: { onChange, value } }) => (
              <FormControlLabel
                control={
                  <Checkbox
                    checked={value || false}
                    onChange={e => onChange(e.target.checked)}
                  />
                }
                label="Featured Post"
              />
            )}
          />
        </Box>

        <Box my={2}>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="blog-image-upload"
            type="file"
            onChange={handleImageChange}
          />
          <label htmlFor="blog-image-upload">
            <Button variant="outlined" component="span">
              {imagePreview ? 'Change Image' : 'Upload Image'}
            </Button>
          </label>

          {imagePreview && (
            <Box mt={2}>
              <img
                src={imagePreview}
                alt="Preview"
                style={{ maxWidth: '100%', maxHeight: '300px' }}
              />
            </Box>
          )}
        </Box>

        <Box mt={3} display="flex" gap={2}>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Changes'}
          </Button>

          <Button
            variant="outlined"
            onClick={() => navigate(-1)}
            disabled={loading}
          >
            Cancel
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default BlogEditPage;
