import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Card,
  CardMedia,
  Typography,
  Button,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  useTheme,
} from '@mui/material';
import { ChevronLeft, ChevronRight, ArrowForward, Close } from '@mui/icons-material';
import { Blog } from '../types';
import { format } from 'date-fns';

interface BlogCarouselProps {
  blogs: Blog[];
  onView?: (blog: Blog) => void;
  isAuthenticated?: boolean;
}

const BlogCarousel: React.FC<BlogCarouselProps> = ({ blogs, onView, isAuthenticated = false }) => {
  const theme = useTheme();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [openDetail, setOpenDetail] = useState<{ open: boolean; blog: Blog | null }>({ 
    open: false, 
    blog: null 
  });

  // Auto-advance carousel
  useEffect(() => {
    if (blogs.length <= 1 || isPaused) return;

    const timer = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % blogs.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [blogs.length, isPaused]);

  const goToNext = useCallback(() => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % blogs.length);
  }, [blogs.length]);

  const goToPrev = useCallback(() => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + blogs.length) % blogs.length);
  }, [blogs.length]);

  const handleOpenDetail = (blog: Blog) => {
    if (onView) {
      onView(blog);
    } else {
      setOpenDetail({ open: true, blog });
    }
  };

  const handleCloseDetail = useCallback(() => {
    setOpenDetail({ open: false, blog: null });
  }, []);

  if (blogs.length === 0) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Typography variant="body1">No blog posts available</Typography>
      </Box>
    );
  }

  const currentBlog = blogs[currentIndex];
  const hasMultipleItems = blogs.length > 1;
  const authorName = currentBlog.author?.name || currentBlog.user?.name || 'Unknown Author';
  const imageUrl = currentBlog.image || '';

  return (
    <Box 
      sx={{ 
        position: 'relative',
        width: '100%',
        maxWidth: '1400px',
        mx: 'auto',
        '&:hover .carousel-arrow': {
          opacity: 1,
        },
      }}
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      {/* Navigation Arrows */}
      {hasMultipleItems && (
        <>
          <IconButton
            className="carousel-arrow"
            onClick={(e) => {
              e.stopPropagation();
              goToPrev();
            }}
            sx={{
              position: 'absolute',
              left: 16,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              color: 'white',
              opacity: 0.7,
              transition: 'opacity 0.3s',
              zIndex: 2,
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.8)',
                opacity: 1,
              },
            }}
          >
            <ChevronLeft fontSize="large" />
          </IconButton>
          <IconButton
            className="carousel-arrow"
            onClick={(e) => {
              e.stopPropagation();
              goToNext();
            }}
            sx={{
              position: 'absolute',
              right: 16,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(0, 0, 0, 0.5)',
              color: 'white',
              opacity: 0.7,
              transition: 'opacity 0.3s',
              zIndex: 2,
              '&:hover': {
                backgroundColor: 'rgba(0, 0, 0, 0.8)',
                opacity: 1,
              },
            }}
          >
            <ChevronRight fontSize="large" />
          </IconButton>
        </>
      )}

      {/* Carousel Item */}
      <Box sx={{ position: 'relative', width: '100%', borderRadius: 2, overflow: 'hidden' }}>
        <Card sx={{ boxShadow: 'none', borderRadius: 0 }}>
          <Box sx={{ position: 'relative', width: '100%', paddingTop: '56.25%' }}>
            <CardMedia
              component="img"
              src={imageUrl}
              alt={currentBlog.title}
              sx={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                transition: 'transform 0.5s ease',
                '&:hover': {
                  transform: 'scale(1.02)',
                },
              }}
              onError={(e) => {
                const target = e.target as HTMLImageElement;
                target.src = '/placeholder.jpg';
              }}/>
            <Box
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                background: 'linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 100%)',
                color: 'white',
                p: 3,
              }}
            >
              <Typography variant="h5" component="h2" sx={{ mb: 1, fontWeight: 'bold' }}>
                {currentBlog.title}
              </Typography>
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleOpenDetail(currentBlog)}
                endIcon={<ArrowForward />}
                sx={{
                  marginTop: 1,
                  backgroundColor: theme.palette.primary.main,
                  '&:hover': { 
                    backgroundColor: theme.palette.primary.dark 
                  },
                }}
              >
                View Details
              </Button>
            </Box>
          </Box>
        </Card>
      </Box>

      {/* Dots Indicator */}
      {hasMultipleItems && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2, gap: 1 }}>
          {blogs.map((_, index) => (
            <Box
              key={index}
              onClick={() => setCurrentIndex(index)}
              sx={{
                width: 10,
                height: 10,
                borderRadius: '50%',
                bgcolor: currentIndex === index ? 'primary.main' : 'action.disabled',
                cursor: 'pointer',
                transition: 'background-color 0.3s',
                '&:hover': { bgcolor: 'primary.dark' },
              }}
            />
          ))}
        </Box>
      )}

      {/* Detail Dialog */}
      <Dialog 
        open={openDetail.open} 
        onClose={handleCloseDetail} 
        maxWidth="md" 
        fullWidth
      >
        <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          {openDetail.blog?.title}
          <IconButton onClick={handleCloseDetail}>
            <Close />
          </IconButton>
        </DialogTitle>
        <DialogContent>
          {openDetail.blog && (
            <Box sx={{ mt: 2 }}>
              {imageUrl && (
                <img
                  src={imageUrl}
                  alt={openDetail.blog.title}
                  style={{ 
                    width: '100%', 
                    maxHeight: '400px',
                    objectFit: 'contain',
                    marginBottom: '16px',
                    borderRadius: '8px'
                  }}
                />
              )}
              {isAuthenticated && openDetail.blog.googleDriveLink && (
                <Button 
                  variant="contained" 
                  color="primary" 
                  href={openDetail.blog.googleDriveLink}
                  target="_blank"
                  rel="noopener noreferrer"
                  fullWidth
                  sx={{ mt: 2 }}
                >
                  View Document
                </Button>
              )}
              <Box sx={{ 
                mt: 3, 
                pt: 2, 
                borderTop: `1px solid ${theme.palette.divider}`,
                display: 'flex',
                justifyContent: 'flex-end',
                alignItems: 'center'
              }}>
                <Typography variant="caption" color="textSecondary">
                  {openDetail.blog.createdAt && 
                    format(new Date(openDetail.blog.createdAt), 'MMMM d, yyyy')}
                </Typography>
              </Box>
            </Box>
          )}
        </DialogContent>
      </Dialog>
    </Box>
  );
};

export default BlogCarousel;
