import React, { useState, useEffect, useRef } from 'react';
import {
  TextField,
  Button,
  Box,
  CircularProgress,
  TextFieldProps,
  Typography,
  IconButton,
  InputAdornment,
  Stack,
} from '@mui/material';
import { CloudUpload, Image, Link as LinkIcon } from '@mui/icons-material';
import { styled } from '@mui/material/styles';
import { BlogFormData } from '../types';
import { isValidGoogleDocsUrl } from '../utils/validation';
import api from '../api';

const VisuallyHiddenInput = styled('input')({
  clip: 'rect(0 0 0 0)',
  clipPath: 'inset(50%)',
  height: 1,
  overflow: 'hidden',
  position: 'absolute',
  bottom: 0,
  left: 0,
  whiteSpace: 'nowrap',
  width: 1,
});

interface BlogFormProps {
  initialData?: BlogFormData;
  onSubmit: (data: BlogFormData) => void;
  onCancel: () => void;
  loading?: boolean;
}

const BlogForm: React.FC<BlogFormProps> = ({
  initialData,
  onSubmit,
  onCancel,
  loading = false,
}) => {
  const [formData, setFormData] = useState<BlogFormData>({
    title: '',
    googleDriveLink: '',
    image: '',
  });
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>('');
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
      if (initialData.image) {
        setImagePreview(initialData.image);
      }
    }
  }, [initialData]);

  useEffect(() => {
    // Create object URL for image preview when file is selected
    if (imageFile) {
      const objectUrl = URL.createObjectURL(imageFile);
      setImagePreview(objectUrl);

      // Clean up object URL to avoid memory leaks
      return () => URL.revokeObjectURL(objectUrl);
    }
  }, [imageFile]);

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.googleDriveLink.trim()) {
      newErrors.googleDriveLink = 'Google Drive link is required';
    } else if (!isValidGoogleDocsUrl(formData.googleDriveLink)) {
      newErrors.googleDriveLink = 'Please enter a valid Google Docs URL';
    }

    if (!imageFile && !formData.image) {
      newErrors.image = 'Please upload an image or provide an image URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange: TextFieldProps['onChange'] = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name as keyof typeof errors]) {
      setErrors((prev) => {
        const newErrors = { ...prev };
        delete newErrors[name as keyof typeof newErrors];
        return newErrors;
      });
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      if (!file.type.startsWith('image/')) {
        setErrors(prev => ({ ...prev, image: 'Please upload an image file' }));
        return;
      }
      setImageFile(file);
      // Clear any previous image URL when a new file is selected
      setFormData(prev => ({ ...prev, image: '' }));
      // Clear any previous errors
      if (errors.image) {
        setErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors.image;
          return newErrors;
        });
      }
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      let imageUrl = formData.image;

      // Upload the image file if a new one was selected
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);

        const response = await api.post('/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
          onUploadProgress: (progressEvent) => {
            const progress = Math.round(
              (progressEvent.loaded * 100) / (progressEvent.total || 1)
            );
            setUploadProgress(progress);
          },
        });

        imageUrl = response.data.url;
      }

      // Submit the form with the new image URL
      await onSubmit({
        ...formData,
        image: imageUrl,
      });

    } catch (error) {
      console.error('Error uploading image:', error);
      setErrors(prev => ({
        ...prev,
        image: 'Failed to upload image. Please try again.',
      }));
    } finally {
      setUploadProgress(0);
    }
  };

  const handlePreview = () => {
    if (formData.image) {
      window.open(formData.image, '_blank');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      <TextField
        margin="normal"
        required
        fullWidth
        id="title"
        label="Blog Title"
        name="title"
        autoComplete="off"
        autoFocus
        value={formData.title}
        onChange={handleChange}
        error={!!errors.title}
        helperText={errors.title}
        disabled={loading}
      />

      <TextField
        margin="normal"
        required
        fullWidth
        name="googleDriveLink"
        label="Google Drive Document Link"
        type="url"
        id="googleDriveLink"
        autoComplete="off"
        value={formData.googleDriveLink}
        onChange={handleChange}
        error={!!errors.googleDriveLink}
        helperText={
          errors.googleDriveLink || 
          'Make sure the document is shared with "Anyone with the link"'
        }
        disabled={loading}
      />

      <Box mt={2} mb={2}>
        <Typography variant="subtitle2" gutterBottom>
          Featured Image
        </Typography>

        <Stack direction="row" spacing={2} alignItems="center" mb={2}>
          <Button
            component="label"
            variant="outlined"
            startIcon={<CloudUpload />}
            disabled={loading}
          >
            Upload Image
            <VisuallyHiddenInput 
              type="file" 
              accept="image/*"
              onChange={handleFileChange}
              ref={fileInputRef}
            />
          </Button>

          <Typography variant="body2" color="text.secondary">
            or
          </Typography>

          <Button
            variant="outlined"
            startIcon={<LinkIcon />}
            onClick={() => {
              setImageFile(null);
              setFormData(prev => ({ ...prev, image: '' }));
              setTimeout(() => {
                const url = prompt('Enter image URL:');
                if (url) {
                  setFormData(prev => ({ ...prev, image: url }));
                }
              }, 0);
            }}
            disabled={loading}
          >
            Use URL
          </Button>
        </Stack>

        {uploadProgress > 0 && uploadProgress < 100 && (
          <Box sx={{ width: '100%', mt: 1 }}>
            <Typography variant="caption" display="block" gutterBottom>
              Uploading: {uploadProgress}%
            </Typography>
            <Box sx={{ width: '100%', bgcolor: '#f0f0f0', borderRadius: 5 }}>
              <Box 
                sx={{
                  width: `${uploadProgress}%`,
                  height: 8,
                  bgcolor: 'primary.main',
                  borderRadius: 5,
                  transition: 'width 0.3s ease-in-out',
                }}
              />
            </Box>
          </Box>
        )}

        {errors.image && (
          <Typography color="error" variant="caption" display="block" gutterBottom>
            {errors.image}
          </Typography>
        )}

        {(imagePreview || formData.image) && !errors.image && (
          <Box mt={2} textAlign="center">
            <Typography variant="caption" display="block" gutterBottom>
              Image Preview:
            </Typography>
            <Box 
              sx={{
                position: 'relative',
                display: 'inline-block',
                maxWidth: '100%',
                maxHeight: '200px',
                border: '1px solid #ddd',
                borderRadius: '4px',
                overflow: 'hidden',
              }}
            >
              <img 
                src={imagePreview || formData.image} 
                alt="Preview" 
                style={{ 
                  maxWidth: '100%', 
                  maxHeight: '200px',
                  display: 'block',
                }} 
                onError={(e) => {
                  const target = e.target as HTMLImageElement;
                  target.style.display = 'none';
                  setErrors(prev => ({
                    ...prev,
                    image: 'Failed to load image. Please check the URL or upload a different image.',
                  }));
                }}
              />
              <Box 
                sx={{
                  position: 'absolute',
                  top: 8,
                  right: 8,
                  bgcolor: 'rgba(0,0,0,0.5)',
                  borderRadius: '50%',
                  width: 32,
                  height: 32,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  '&:hover': {
                    bgcolor: 'rgba(0,0,0,0.7)',
                  },
                }}
                onClick={() => {
                  setImageFile(null);
                  setImagePreview('');
                  setFormData(prev => ({ ...prev, image: '' }));
                  if (fileInputRef.current) {
                    fileInputRef.current.value = '';
                  }
                }}
              >
                <Typography color="white" variant="body2">Ã—</Typography>
              </Box>
            </Box>
          </Box>
        )}
      </Box>

      <Box mt={3} display="flex" justifyContent="flex-end" gap={2}>
        <Button
          onClick={onCancel}
          variant="outlined"
          disabled={loading}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={loading}
        >
          {loading ? (
            <CircularProgress size={24} />
          ) : initialData ? (
            'Update Post'
          ) : (
            'Create Post'
          )}
        </Button>
      </Box>
    </Box>
  );
};

export default BlogForm;
