import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  useTheme,
  Box,
  useMediaQuery,
  IconButton
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Image as ImageIcon, Visibility as VisibilityIcon, Close as CloseIcon } from '@mui/icons-material';
import { Blog } from '../types';

interface BlogImage {
  data?: string;
  contentType?: string;
  filename?: string;
}

interface BlogCardProps {
  blog: Omit<Blog, 'image'> & { 
    _id?: string;
    image?: string | BlogImage;
  };
  onEdit: () => void;
  onDelete: () => void;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete }) => {
  const [openPreview, setOpenPreview] = useState(false);
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));

  const formatDate = (dateString: string) => {
    try {
      return new Date(dateString).toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  // Extract Google Docs ID from URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      const urlObj = new URL(url);
      const pathParts = urlObj.pathname.split('/').filter(Boolean);
      let docId = pathParts[pathParts.length - 1];

      // Handle different Google Docs URL formats
      if (docId === 'edit') {
        docId = pathParts[pathParts.length - 2];
      }

      if (!docId) return '';
      return `https://docs.google.com/document/d/${docId}/preview`;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return '';
    }
  };

  const handleDeleteClick = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await onDelete();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  // Handle different image data formats
  useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      return;
    }

    if (typeof blog.image === 'string') {
      // Handle data URLs or external URLs
      if (blog.image.startsWith('data:image/')) {
        setImageSrc(blog.image);
      } else if (blog.image.startsWith('http')) {
        // Fallback for any remaining external URLs (shouldn't happen with new system)
        setImageSrc(blog.image);
      } else {
        // Handle base64 string without data URL prefix
        setImageSrc(`data:image/jpeg;base64,${blog.image}`);
      }
    } else if (blog.image && typeof blog.image === 'object' && 'data' in blog.image) {
      // Handle image object with data and contentType
      const base64Data = blog.image.data;
      const mimeType = blog.image.contentType || 'image/jpeg';
      // Check if the data is already a data URL
      if (typeof base64Data === 'string' && base64Data.startsWith('data:image/')) {
        setImageSrc(base64Data);
      } else {
        setImageSrc(`data:${mimeType};base64,${base64Data}`);
      }
    } else {
      setImageSrc('');
    }

    // Reset error state when image changes
    setImageError(false);
  }, [blog.image]);

  return (
    <>
      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {imageSrc && !imageError ? (
          <Box sx={{ mt: 2, position: 'relative', width: '100%', paddingTop: '56.25%' }}>
            <img
              src={imageSrc}
              alt={blog.title}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'contain',
                backgroundColor: '#f5f5f5',
                borderRadius: theme.shape.borderRadius,
                maxHeight: '300px',
                margin: '0 auto',
                display: 'block'
              }}
              onError={() => {
                console.error('Error loading image:', blog.image);
                setImageError(true);
              }}
            />
          </Box>
        ) : (
          <Box sx={{
            backgroundColor: 'grey.200',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            paddingTop: '56.25%',
            position: 'relative',
            minHeight: 200
          }}>
            <ImageIcon sx={{ fontSize: 48, color: 'grey.400' }} />
          </Box>
        )}
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography gutterBottom variant="h6" component="h2" noWrap>
            {blog.title}
          </Typography>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Created: {formatDate(blog.createdAt)}
          </Typography>
          {blog.updatedAt && (
            <Typography variant="caption" color="text.secondary" display="block">
              Updated: {formatDate(blog.updatedAt)}
            </Typography>
          )}
        </CardContent>
        <CardActions sx={{ mt: 'auto', justifyContent: 'space-between' }}>
          <Button 
            size="small" 
            color="primary"
            startIcon={<VisibilityIcon />}
            onClick={() => setOpenPreview(true)}
          >
            View
          </Button>
          <Box>
            <IconButton color="primary" onClick={onEdit}>
              <EditIcon fontSize="small" />
            </IconButton>
            <IconButton color="error" onClick={handleDeleteClick}>
              <DeleteIcon fontSize="small" />
            </IconButton>
          </Box>
        </CardActions>
      </Card>

      {/* Preview Dialog */}
      <Dialog
        fullScreen={fullScreen}
        open={openPreview}
        onClose={() => setOpenPreview(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            {blog.title}
            <IconButton edge="end" onClick={() => setOpenPreview(false)}>
              <CloseIcon />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Box mb={2}>
            {blog.image && (
              <img 
                src={typeof blog.image === 'string' ? blog.image : ''} 
                alt={blog.title}
                style={{ 
                  width: '100%', 
                  maxHeight: '300px',
                  objectFit: 'contain',
                  borderRadius: '4px',
                }} 
              />
            )}
          </Box>
          <Box 
            component="iframe"
            src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
            title={blog.title}
            width="100%"
            height="600px"
            frameBorder="0"
            sx={{
              border: '1px solid #e0e0e0',
              borderRadius: 1,
              mt: 2,
            }}
            allowFullScreen
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenPreview(false)}>Close</Button>
          <Button 
            variant="contained" 
            color="primary" 
            href={blog.googleDriveLink} 
            target="_blank"
            rel="noopener noreferrer"
          >
            Open in Google Docs
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default BlogCard;
