import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  useTheme,
  Box,
  IconButton,
  Tooltip,
  CardMedia,
  Chip
} from '@mui/material';
import { 
  Edit as EditIcon, 
  Delete as DeleteIcon, 
  Image as ImageIcon,
  AccessTime as AccessTimeIcon,
  Update as UpdateIcon,
  ArrowForward as ArrowForwardIcon
} from '@mui/icons-material';
import { Blog } from '../types';

interface BlogCardProps {
  blog: Blog;
  onEdit?: () => void;
  onDelete?: () => Promise<void>;
  showContent?: boolean;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete, showContent = false }) => {
  const [openDetail, setOpenDetail] = useState(false);
  const theme = useTheme();
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Handle image source extraction
  useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      return;
    }

    if (typeof blog.image === 'string') {
      setImageSrc(blog.image);
    } else if ('data' in blog.image) {
      setImageSrc(`data:${blog.image.contentType || 'image/jpeg'};base64,${blog.image.data}`);
    }
  }, [blog.image]);

  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  const handleDeleteClick = async () => {
    if (!onDelete) return;

    const confirmed = window.confirm('Are you sure you want to delete this blog post?');
    if (!confirmed) return;

    try {
      await onDelete();
    } catch (error) {
      console.error('Error deleting blog:', error);
    }
  };

  return (
    <>
      <Card 
        sx={{ 
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          transition: 'transform 0.3s ease, box-shadow 0.3s ease',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: theme.shadows[8],
          },
          position: 'relative',
          overflow: 'hidden',
        }}
      >
        {/* Image Section */}
        <Box sx={{ position: 'relative', paddingTop: '100%' }}>
          {imageSrc ? (
            <CardMedia
              component="img"
              image={imageError ? '/placeholder.jpg' : imageSrc}
              alt={blog.title}
              onError={() => setImageError(true)}
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'cover',
              }}
            />
          ) : (
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                bgcolor: theme.palette.grey[200],
              }}
            >
              <ImageIcon sx={{ fontSize: 48, color: theme.palette.grey[400] }} />
            </Box>
          )}

          {/* Overlay with gradient */}
          <Box
            sx={{
              position: 'absolute',
              bottom: 0,
              left: 0,
              right: 0,
              height: '50%',
              background: 'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, transparent 100%)',
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'flex-end',
              p: 2,
            }}
          >
            <Typography 
              variant="h6" 
              component="h3" 
              sx={{ 
                color: 'white',
                fontWeight: 600,
                mb: 1,
                textShadow: '0 1px 3px rgba(0,0,0,0.5)',
                lineHeight: 1.2,
              }}
            >
              {blog.title}
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, color: 'rgba(255,255,255,0.8)' }}>
              <AccessTimeIcon fontSize="small" />
              <Typography variant="caption">
                {formatDate(blog.createdAt)}
              </Typography>
            </Box>

            {(onEdit || onDelete) && (
              <Box sx={{ position: 'absolute', top: 8, right: 8, display: 'flex', gap: 1 }}>
                {onEdit && (
                  <Tooltip title="Edit">
                    <IconButton 
                      size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        onEdit();
                      }}
                      sx={{
                        color: 'white',
                        backgroundColor: 'rgba(0,0,0,0.4)',
                        '&:hover': {
                          backgroundColor: 'rgba(0,0,0,0.6)',
                        },
                      }}
                    >
                      <EditIcon fontSize="small" />
                    </IconButton>
                  </Tooltip>
                )}
                {onDelete && (
                  <Tooltip title="Delete">
                    <IconButton 
                      size="small"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteClick();
                      }}
                      sx={{
                        color: 'white',
                        backgroundColor: 'rgba(0,0,0,0.4)',
                        '&:hover': {
                          backgroundColor: 'rgba(0,0,0,0.6)',
                        },
                      }}
                    >
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Tooltip>
                )}
              </Box>
            )}
          </Box>
        </Box>

        {/* Content Section */}
        <CardContent sx={{ flexGrow: 1, p: 2 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 1 }}>
            {blog.author && (
              <Chip 
                label={blog.author.name || 'Unknown Author'}
                size="small"
                sx={{ 
                  backgroundColor: theme.palette.primary.light,
                  color: 'white',
                  fontWeight: 500,
                }}
              />
            )}
            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                <UpdateIcon fontSize="small" color="action" />
                <Typography variant="caption" color="textSecondary">
                  Updated
                </Typography>
              </Box>
            )}
          </Box>

          {showContent ? (
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              {blog.googleDriveLink}
            </Typography>
          ) : (
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2, fontStyle: 'italic' }}>
              Sign in to view the document
            </Typography>
          )}
        </CardContent>

        <CardActions sx={{ p: 2, pt: 0, justifyContent: 'flex-end' }}>
          <Button 
            size="small" 
            endIcon={<ArrowForwardIcon />}
            onClick={() => setOpenDetail(true)}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
              '&:hover': {
                backgroundColor: 'transparent',
                textDecoration: 'underline',
              },
            }}
          >
            {showContent ? 'Read More' : 'View Details'}
          </Button>
        </CardActions>
      </Card>

      {/* Blog Detail Modal */}
      {openDetail && (
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.7)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: theme.zIndex.modal,
            p: 2,
          }}
          onClick={() => setOpenDetail(false)}
        >
          <Box
            onClick={(e) => e.stopPropagation()}
            sx={{
              backgroundColor: 'white',
              borderRadius: 2,
              maxWidth: 800,
              width: '100%',
              maxHeight: '90vh',
              overflowY: 'auto',
              position: 'relative',
            }}
          >
            <IconButton
              onClick={() => setOpenDetail(false)}
              sx={{
                position: 'absolute',
                right: 8,
                top: 8,
                zIndex: 1,
                backgroundColor: 'rgba(0,0,0,0.1)',
                '&:hover': {
                  backgroundColor: 'rgba(0,0,0,0.2)',
                },
              }}
            >
              <DeleteIcon />
            </IconButton>

            <Box sx={{ p: 4 }}>
              <Typography variant="h4" component="h1" gutterBottom>
                {blog.title}
              </Typography>

              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 3 }}>
                {blog.author && (
                  <Chip 
                    label={`By ${blog.author.name || 'Unknown Author'}`}
                    size="small"
                    sx={{ 
                      backgroundColor: theme.palette.primary.light,
                      color: 'white',
                      fontWeight: 500,
                    }}
                  />
                )}

                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <AccessTimeIcon fontSize="small" color="action" />
                  <Typography variant="body2" color="textSecondary">
                    {formatDate(blog.createdAt)}
                    {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
                      <span> • Updated {formatDate(blog.updatedAt)}</span>
                    )}
                  </Typography>
                </Box>
              </Box>

              {imageSrc && (
                <Box sx={{ mb: 3, borderRadius: 2, overflow: 'hidden' }}>
                  <img 
                    src={imageError ? '/placeholder.jpg' : imageSrc} 
                    alt={blog.title}
                    onError={() => setImageError(true)}
                    style={{ width: '100%', height: 'auto', display: 'block' }}
                  />
                </Box>
              )}

              {showContent ? (
                <Typography variant="body1" paragraph>
                  {blog.googleDriveLink}
                </Typography>
              ) : (
                <Box sx={{ 
                  backgroundColor: theme.palette.grey[100], 
                  p: 2, 
                  borderRadius: 1,
                  textAlign: 'center',
                  my: 2
                }}>
                  <Typography variant="body1" color="text.secondary">
                    Please sign in to view the document
                  </Typography>
                  <Button 
                    variant="contained" 
                    color="primary" 
                    sx={{ mt: 2 }}
                    onClick={() => {
                      // Redirect to login or show login dialog
                      window.location.href = '/login';
                    }}
                  >
                    Sign In
                  </Button>
                </Box>
              )}

              <Box sx={{ mt: 4, display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                <Button 
                  variant="outlined" 
                  onClick={() => setOpenDetail(false)}
                >
                  Close
                </Button>
                {showContent ? (
                  <Button 
                    variant="contained" 
                    color="primary"
                    href={blog.googleDriveLink}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Open in Google Drive
                  </Button>
                ) : (
                  <Button 
                    variant="contained" 
                    color="primary"
                    onClick={() => {
                      // Redirect to login or show login dialog
                      window.location.href = '/login';
                    }}
                  >
                    Sign In to View
                  </Button>
                )}
              </Box>
            </Box>
          </Box>
        </Box>
      )}
    </>
  );
};

export default BlogCard;
