import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  IconButton,
  useTheme,
  useMediaQuery,
  CircularProgress,
  Divider,
  Chip,
  Avatar,
  Tooltip
} from '@mui/material';
import { 
  Close as CloseIcon, 
  OpenInNew as OpenInNewIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  Image as ImageIcon
} from '@mui/icons-material';
import { format } from 'date-fns';
import { Blog } from '../types';

interface BlogDetailProps {
  blog: Blog;
  open: boolean;
  onClose: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const BlogDetail: React.FC<BlogDetailProps> = ({ blog, open, onClose, onEdit, onDelete }) => {
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));
  const [loading, setLoading] = useState(true);
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Format date for display
  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMMM d, yyyy');
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  // Get Google Docs embed URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      const urlObj = new URL(url);
      const pathParts = urlObj.pathname.split('/').filter(Boolean);
      let docId = pathParts[pathParts.length - 1];
      
      // Handle different Google Docs URL formats
      if (docId === 'edit') {
        docId = pathParts[pathParts.length - 2];
      }
      
      if (!docId) return '';
      return `https://docs.google.com/document/d/${docId}/preview`;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return '';
    }
  };

  // Handle image loading
  useEffect(() => {
    if (!blog?.image) {
      setImageSrc('');
      setLoading(false);
      return;
    }

    const loadImage = async () => {
      try {
        setLoading(true);
        setImageError(false);
        
        if (typeof blog.image === 'string') {
          // Handle string image (URL or base64)
          if (blog.image.startsWith('data:image/')) {
            setImageSrc(blog.image);
          } else if (blog.image.startsWith('http')) {
            // External URL
            setImageSrc(blog.image);
          } else {
            // Base64 without data URL prefix
            setImageSrc(`data:image/jpeg;base64,${blog.image}`);
          }
        } else if (blog.image && 'data' in blog.image) {
          // Handle BlogImage type
          const { data, contentType } = blog.image;
          const mimeType = contentType || 'image/jpeg';

          if (!data) {
            throw new Error('No image data available');
          }

          if (typeof data === 'string') {
            if (data.startsWith('data:image/')) {
              setImageSrc(data);
            } else {
              setImageSrc(`data:${mimeType};base64,${data}`);
            }
          } else if (data instanceof ArrayBuffer) {
            // Handle ArrayBuffer data
            const base64String = btoa(
              new Uint8Array(data).reduce(
                (data, byte) => data + String.fromCharCode(byte),
                ''
              )
            );
            setImageSrc(`data:${mimeType};base64,${base64String}`);
          }
        }
      } catch (error) {
        console.error('Error loading image:', error);
        setImageError(true);
      } finally {
        setLoading(false);
      }
    };

    loadImage();

    // Cleanup function
    return () => {
      setImageSrc('');
      setLoading(true);
    };
  }, [blog?.image]);

  // Handle iframe load and errors
  const handleIframeLoad = () => {
    setLoading(false);
  };

  const handleIframeError = () => {
    console.error('Failed to load Google Docs content');
    setLoading(false);
  };

  // Handle delete confirmation
  const handleDeleteClick = () => {
    if (window.confirm('Are you sure you want to delete this blog post? This action cannot be undone.')) {
      onDelete?.();
    }
  };

  return (
    <Dialog
      fullScreen={fullScreen}
      open={open}
      onClose={onClose}
      maxWidth="lg"
      fullWidth
      PaperProps={{
        sx: {
          height: '90vh',
          maxHeight: '900px',
          borderRadius: 2,
          overflow: 'hidden',
        },
      }}
    >
      <DialogTitle sx={{ 
        backgroundColor: theme.palette.primary.main,
        color: theme.palette.primary.contrastText,
        py: 1.5,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}>
        <Typography variant="h6" component="div" noWrap sx={{ flex: 1, mr: 2 }}>
          {blog.title}
        </Typography>
        <IconButton edge="end" onClick={onClose} color="inherit">
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers sx={{ p: 0, display: 'flex', flexDirection: 'column', flex: 1 }}>
        {/* Blog Metadata */}
        <Box sx={{ p: 3, pb: 1 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            {blog.author && (
              <>
                <Avatar 
                  sx={{ 
                    width: 32, 
                    height: 32, 
                    mr: 1.5,
                    backgroundColor: theme.palette.primary.main,
                    color: theme.palette.primary.contrastText,
                    fontSize: '0.875rem'
                  }}
                >
                  {blog.author.name ? blog.author.name.charAt(0).toUpperCase() : 'U'}
                </Avatar>
                <Typography variant="subtitle2" color="text.secondary">
                  {blog.author.name || 'Unknown Author'}
                </Typography>
                <Divider orientation="vertical" flexItem sx={{ mx: 1.5 }} />
              </>
            )}
            <Typography variant="caption" color="text.secondary">
              {formatDate(blog.createdAt)}
            </Typography>
            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <>
                <Divider orientation="vertical" flexItem sx={{ mx: 1.5 }} />
                <Typography variant="caption" color="text.secondary">
                  Updated: {formatDate(blog.updatedAt)}
                </Typography>
              </>
            )}
          </Box>
        </Box>

        {/* Featured Image */}
        {!loading && imageSrc && !imageError && (
          <Box sx={{ 
            width: '100%', 
            maxHeight: '300px',
            overflow: 'hidden',
            backgroundColor: theme.palette.grey[100],
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            borderTop: `1px solid ${theme.palette.divider}`,
            borderBottom: `1px solid ${theme.palette.divider}`,
          }}>
            <img
              src={imageSrc}
              alt={blog.title}
              style={{
                maxWidth: '100%',
                maxHeight: '300px',
                objectFit: 'contain',
                display: 'block',
              }}
              onError={() => setImageError(true)}
            />
          </Box>
        )}

        {/* Loading State */}
        {loading && (
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            minHeight: 200,
            backgroundColor: theme.palette.grey[50],
          }}>
            <CircularProgress />
          </Box>
        )}

        {/* Blog Content */}
        <Box sx={{ flex: 1, position: 'relative', overflow: 'hidden' }}>
          {!loading && (
            <Box
              component="iframe"
              src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
              title={blog.title}
              width="100%"
              height="100%"
              frameBorder="0"
              onLoad={handleIframeLoad}
              onError={handleIframeError}
              style={{
                border: 'none',
                minHeight: '400px',
                backgroundColor: theme.palette.background.paper,
                visibility: loading ? 'hidden' : 'visible',
              }}
              loading="eager"
              allowFullScreen
            />
          )}
          {/* Loading Overlay */}
          {loading && (
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(255, 255, 255, 0.7)',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                zIndex: 1,
              }}
            >
              <CircularProgress />
            </Box>
          )}
        </Box>
      </DialogContent>

      <DialogActions sx={{ 
        px: 2, 
        py: 1,
        borderTop: `1px solid ${theme.palette.divider}`,
        justifyContent: 'space-between',
        backgroundColor: theme.palette.background.paper,
        gap: 1,
      }}>
        <Box display="flex" gap={1}>
          <Button
            variant="outlined"
            color="primary"
            size="small"
            href={blog.googleDriveLink}
            target="_blank"
            rel="noopener noreferrer"
            startIcon={<OpenInNewIcon fontSize="small" />}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
            }}
          >
            Open in Google Docs
          </Button>
        </Box>

        <Box display="flex" gap={1}>
          {onEdit && (
            <Tooltip title="Edit Blog Post">
              <IconButton 
                onClick={onEdit} 
                size="small"
                color="primary"
                sx={{
                  border: `1px solid ${theme.palette.primary.main}33`,
                  '&:hover': {
                    backgroundColor: `${theme.palette.primary.main}11`,
                  },
                }}
              >
                <EditIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
          {onDelete && (
            <Tooltip title="Delete Blog Post">
              <IconButton 
                onClick={handleDeleteClick} 
                size="small"
                color="error"
                sx={{
                  border: `1px solid ${theme.palette.error.main}33`,
                  '&:hover': {
                    backgroundColor: `${theme.palette.error.main}11`,
                  },
                }}
              >
                <DeleteIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          )}
        </Box>
      </DialogActions>
    </Dialog>
  );
};

export default BlogDetail;
