import React, { useState, useEffect } from 'react';
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  CardActions,
  Button,
  Box,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  useTheme,
  Avatar,
  Chip,
  Tooltip
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Image as ImageIcon, Visibility, Close } from '@mui/icons-material';
import { formatDistanceToNow } from 'date-fns';
import { Blog } from '../types/blog';

interface BlogImage {
  data?: string;
  contentType?: string;
  filename?: string;
}

interface BlogCardProps {
  blog: Omit<Blog, 'image'> & { 
    _id?: string;
    image?: string | BlogImage;
  };
  onEdit: () => void;
  onDelete: () => void;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete }) => {
  const [openPreview, setOpenPreview] = useState(false);
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);
  const theme = useTheme();
  const fullScreen = useTheme().breakpoints.down('md');

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  // Extract Google Docs ID from URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      const urlObj = new URL(url);
      const pathParts = urlObj.pathname.split('/').filter(Boolean);
      let docId = pathParts[pathParts.length - 1];

      // Handle different Google Docs URL formats
      if (docId === 'edit') {
        docId = pathParts[pathParts.length - 2];
      }

      if (!docId) return '';
      return `https://docs.google.com/document/d/${docId}/preview`;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return '';
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        onDelete();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  // Handle different image data formats
  useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      return;
    }

    if (typeof blog.image === 'string') {
      // Handle string URLs
      setImageSrc(blog.image);
    } else if (blog.image?.data) {
      // Handle base64 image data
      const base64Data = blog.image.data;
      const mimeType = blog.image.contentType || 'image/jpeg';
      setImageSrc(`data:${mimeType};base64,${base64Data}`);
    } else {
      setImageSrc('');
    }
  }, [blog.image]);

  return (
    <>
      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {imageSrc && !imageError ? (
          <Box sx={{ mt: 2, position: 'relative', width: '100%', paddingTop: '56.25%' }}>
            <img
              src={imageSrc}
              alt={blog.title}
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'contain',
                backgroundColor: '#f5f5f5',
                borderRadius: theme.shape.borderRadius
              }}
              onError={() => setImageError(true)}
            />
          </Box>
        ) : (
          <Box sx={{
            backgroundColor: 'grey.200',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            paddingTop: '56.25%',
            position: 'relative',
            minHeight: 200
          }}>
            <ImageIcon sx={{ fontSize: 48, color: 'grey.400' }} />
          </Box>
        )}
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography gutterBottom variant="h6" component="h2" noWrap>
            {blog.title}
          </Typography>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Created: {formatDate(blog.createdAt)}
          </Typography>
          {blog.updatedAt && (
            <Typography variant="caption" color="text.secondary" display="block">
              Updated: {formatDate(blog.updatedAt)}
            </Typography>
          )}
        </CardContent>
        <CardActions sx={{ mt: 'auto', justifyContent: 'space-between' }}>
          <Button 
            size="small" 
            color="primary"
            startIcon={<Visibility />}
            onClick={() => setOpenPreview(true)}
          >
            View
          </Button>
          <Box>
            <IconButton size="small" onClick={onEdit} color="primary">
              <Edit fontSize="small" />
            </IconButton>
            <IconButton size="small" onClick={onDelete} color="error">
              <Delete fontSize="small" />
            </IconButton>
          </Box>
        </CardActions>
      </Card>

      {/* Preview Dialog */}
      <Dialog
        fullScreen={fullScreen}
        open={openPreview}
        onClose={() => setOpenPreview(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            {blog.title}
            <IconButton edge="end" onClick={() => setOpenPreview(false)}>
              <Close />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Box mb={2}>
            {blog.image && (
              <img 
                src={blog.image} 
                alt={blog.title}
                style={{ 
                  width: '100%', 
                  maxHeight: '300px',
                  objectFit: 'contain',
                  borderRadius: '4px',
                }} 
              />
            )}
          </Box>
          <Box 
            component="iframe"
            src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
            title={blog.title}
            width="100%"
            height="600px"
            frameBorder="0"
            sx={{
              border: '1px solid #e0e0e0',
              borderRadius: 1,
              mt: 2,
            }}
            allowFullScreen
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenPreview(false)}>Close</Button>
          <Button 
            variant="contained" 
            color="primary" 
            href={blog.googleDriveLink} 
            target="_blank"
            rel="noopener noreferrer"
          >
            Open in Google Docs
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default BlogCard;
