import React, { useState } from 'react';
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  CardActions,
  Button,
  Box,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  useTheme,
  useMediaQuery,
} from '@mui/material';
import { Edit, Delete, Visibility, Close } from '@mui/icons-material';
import { Blog } from '../types';

interface BlogCardProps {
  blog: Blog & { _id?: string };
  onEdit: () => void;
  onDelete: () => void;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete }) => {
  const [openPreview, setOpenPreview] = useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  // Extract Google Docs ID from URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      const urlObj = new URL(url);
      const pathParts = urlObj.pathname.split('/').filter(Boolean);
      let docId = pathParts[pathParts.length - 1];

      // Handle different Google Docs URL formats
      if (docId === 'edit') {
        docId = pathParts[pathParts.length - 2];
      }

      if (!docId) return '';
      return `https://docs.google.com/document/d/${docId}/preview`;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return '';
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        onDelete();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  const [imageError, setImageError] = useState(false);
  const [imageUrl, setImageUrl] = useState<string | null>(null);

  // Process image URL when component mounts or blog changes
  useEffect(() => {
    if (blog.image) {
      // If it's a Google Drive URL, convert it to a direct image URL
      if (blog.image.includes('drive.google.com')) {
        const fileId = blog.image.match(/[\w\-]{25,}/);
        if (fileId) {
          setImageUrl(`https://drive.google.com/uc?export=view&id=${fileId[0]}`);
        } else {
          setImageUrl(blog.image);
        }
      } else {
        setImageUrl(blog.image);
      }
      setImageError(false);
    }
  }, [blog.image]);

  return (
    <>
      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {imageUrl && !imageError ? (
          <Box sx={{ position: 'relative', paddingTop: '56.25%' }}> {/* 16:9 Aspect Ratio */}
            <CardMedia
              component="img"
              image={imageUrl}
              alt={blog.title}
              onError={() => setImageError(true)}
              sx={{ 
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'cover'
              }}
            />
          </Box>
        ) : (
          <Box 
            sx={{ 
              height: 140, 
              bgcolor: 'grey.200',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <Typography variant="body2" color="text.secondary">
              {imageError ? 'Image not available' : 'No image'}
            </Typography>
          </Box>
        )}
        <CardContent sx={{ flexGrow: 1 }}>
          <Typography gutterBottom variant="h6" component="h2" noWrap>
            {blog.title}
          </Typography>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Created: {formatDate(blog.createdAt)}
          </Typography>
          {blog.updatedAt && (
            <Typography variant="caption" color="text.secondary" display="block">
              Updated: {formatDate(blog.updatedAt)}
            </Typography>
          )}
        </CardContent>
        <CardActions sx={{ mt: 'auto', justifyContent: 'space-between' }}>
          <Button 
            size="small" 
            color="primary"
            startIcon={<Visibility />}
            onClick={() => setOpenPreview(true)}
          >
            View
          </Button>
          <Box>
            <IconButton size="small" onClick={onEdit} color="primary">
              <Edit fontSize="small" />
            </IconButton>
            <IconButton size="small" onClick={onDelete} color="error">
              <Delete fontSize="small" />
            </IconButton>
          </Box>
        </CardActions>
      </Card>

      {/* Preview Dialog */}
      <Dialog
        fullScreen={fullScreen}
        open={openPreview}
        onClose={() => setOpenPreview(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box display="flex" justifyContent="space-between" alignItems="center">
            {blog.title}
            <IconButton edge="end" onClick={() => setOpenPreview(false)}>
              <Close />
            </IconButton>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Box mb={2}>
            {blog.image && (
              <img 
                src={blog.image} 
                alt={blog.title}
                style={{ 
                  width: '100%', 
                  maxHeight: '300px',
                  objectFit: 'contain',
                  borderRadius: '4px',
                }} 
              />
            )}
          </Box>
          <Box 
            component="iframe"
            src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
            title={blog.title}
            width="100%"
            height="600px"
            frameBorder="0"
            sx={{
              border: '1px solid #e0e0e0',
              borderRadius: 1,
              mt: 2,
            }}
            allowFullScreen
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenPreview(false)}>Close</Button>
          <Button 
            variant="contained" 
            color="primary" 
            href={blog.googleDriveLink} 
            target="_blank"
            rel="noopener noreferrer"
          >
            Open in Google Docs
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default BlogCard;
