import { Blog, BlogFormData } from '../types';
import api from '../api';

export const getBlogs = async (): Promise<Blog[]> => {
  try {
    const response = await api.get('/api/blogs');
    return response.data || [];
  } catch (error) {
    console.error('Error fetching blogs:', error);
    throw error;
  }
};

export const getBlogById = async (id: string): Promise<Blog> => {
  try {
    const response = await api.get(`/api/blogs/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching blog with id ${id}:`, error);
    throw error;
  }
};

export const createBlog = async (blogData: BlogFormData): Promise<Blog> => {
  try {
    const formData = new FormData();

    // Append all fields from blogData to formData
    Object.entries(blogData).forEach(([key, value]) => {
      if (key === 'image' && value instanceof File) {
        formData.append('image', value);
      } else if (value !== undefined && value !== null) {
        formData.append(key, String(value));
      }
    });

    const response = await api.post('/api/blogs', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error creating blog:', error);
    throw error;
  }
};

export const updateBlog = async (id: string, blogData: Partial<BlogFormData>): Promise<Blog> => {
  try {
    const formData = new FormData();

    // Append all fields from blogData to formData
    Object.entries(blogData).forEach(([key, value]) => {
      if (key === 'image' && value instanceof File) {
        formData.append('image', value);
      } else if (value !== undefined && value !== null) {
        formData.append(key, String(value));
      }
    });

    const response = await api.put(`/api/blogs/${id}`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
    return response.data;
  } catch (error) {
    console.error(`Error updating blog with id ${id}:`, error);
    throw error;
  }
};

export const deleteBlog = async (id: string): Promise<void> => {
  try {
    await api.delete(`/api/blogs/${id}`);
  } catch (error) {
    console.error(`Error deleting blog with id ${id}:`, error);
    throw error;
  }
};
