import React, { useState, useEffect } from 'react';
import api from '../api';
import {
  Typography,
  CircularProgress,
  Box,
  Grid,
  Snackbar,
  Alert,
} from '@mui/material';
import BlogCard from '../components/BlogCard';
import { Blog } from '../types';
import BlogCarousel from '../components/BlogCarousel';

interface BlogResponse {
  _id: string;
  title: string;
  googleDriveLink: string;
  image?: {
    data: string;
    contentType: string;
    filename?: string;
  } | string;
  createdAt: string;
  updatedAt?: string;
  author?: {
    id: string;
    name: string;
    email: string;
  };
}

const BlogsPage: React.FC = () => {
  const [blogs, setBlogs] = useState<Blog[]>([]);
  const [loading, setLoading] = useState(true);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState<'success' | 'error'>('success');
  const [error, setError] = useState<string | null>(null);

  // No need for user check here since we're not showing any user-specific UI

  // Fetch blogs from API
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const response = await api.get('/api/blogs');
        const blogsWithId = response.data.map((blog: BlogResponse): Blog => {
          let imageData = '';

          if (blog.image) {
            if (typeof blog.image === 'string') {
              imageData = blog.image;
            } else if ('data' in blog.image) {
              imageData = `data:${blog.image.contentType || 'image/jpeg'};base64,${blog.image.data}`;
            }
          }

          return {
            id: blog._id,
            title: blog.title,
            googleDriveLink: blog.googleDriveLink,
            image: imageData,
            createdAt: blog.createdAt,
            updatedAt: blog.updatedAt,
            author: blog.author
          };
        });

        setBlogs(blogsWithId);
      } catch (error) {
        console.error('Error fetching blogs:', error);
        setError('Failed to load blogs. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  // Simple view handler for blog posts
  const handleViewBlog = (blog: Blog) => {
    // You can implement a detail view here if needed
    console.log('Viewing blog:', blog.id);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1">
          Blog Posts
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {blogs.length === 0 ? (
        <Box textAlign="center" py={4}>
          <Typography variant="h6" color="textSecondary">
            No blog posts yet. {user && 'Create your first post!'}
          </Typography>
        </Box>
      ) : (
        <>
          {/* Carousel Section */}
          <Box mb={6}>
            <Typography variant="h5" gutterBottom>
              Featured Posts
            </Typography>
            <BlogCarousel 
              blogs={blogs.slice(0, 5)}
              onView={handleViewBlog}
            />
          </Box>

          {/* Grid Section */}
          <Box mt={6}>
            <Typography variant="h5" gutterBottom>
              {user ? 'All Posts' : 'Latest Posts'}
            </Typography>
            <Grid container spacing={3}>
              {blogs.map((blog) => (
            <Grid item xs={12} sm={6} md={4} key={blog.id}>
              <BlogCard
                blog={{
                  ...blog,
                  // Always hide Google Drive link in public view
                  googleDriveLink: ''
                }}
                onView={() => handleViewBlog(blog)}
                showContent={false} // Always hide content in public view
              />
            </Grid>
          ))}
            </Grid>
          </Box>
        </>
      )}


    </Box>
  );
};

export default BlogsPage;
