import React, { useState, useEffect } from 'react';
import api from 'api';
import {
  Button,
  Typography,
  CircularProgress,
  Box,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import BlogForm from '../components/BlogForm.tsx';
import BlogCard from '../components/BlogCard.tsx';
import { Blog } from '../types';

const BlogsPage: React.FC = () => {
  const [blogs, setBlogs] = useState<Blog[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [openForm, setOpenForm] = useState<boolean>(false);
  const [editingBlog, setEditingBlog] = useState<Blog | null>(null);

  // Fetch blogs from API
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const response = await api.get('/api/blogs');
        setBlogs(response.data);
      } catch (error) {
        console.error('Error fetching blogs:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  const handleCreateBlog = () => {
    setEditingBlog(null);
    setOpenForm(true);
  };

  const handleEditBlog = (blog: Blog) => {
    setEditingBlog(blog);
    setOpenForm(true);
  };

  const handleDeleteBlog = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await api.delete(`/api/blogs/${id}`);
        setBlogs(blogs.filter(blog => blog.id !== id));
      } catch (error) {
        console.error('Error deleting blog:', error);
        // Add error handling (e.g., show error message to user)
      }
    }
  };

  const handleFormSubmit = async (blogData: Omit<Blog, 'id' | 'createdAt'>) => {
    try {
      if (editingBlog) {
        // Update existing blog
        const response = await api.put(`/api/blogs/${editingBlog.id}`, blogData);
        setBlogs(blogs.map(blog => 
          blog.id === editingBlog.id ? response.data : blog
        ));
      } else {
        // Create new blog
        const response = await api.post('/api/blogs', blogData);
        setBlogs([...blogs, response.data]);
      }
      setOpenForm(false);
    } catch (error) {
      console.error('Error saving blog:', error);
      // Add error handling (e.g., show error message to user)
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <div>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4" component="h1" gutterBottom>
          My Blog Posts
        </Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={handleCreateBlog}
        >
          New Post
        </Button>
      </Box>

      {blogs.length === 0 ? (
        <Box textAlign="center" py={4}>
          <Typography variant="h6" color="textSecondary">
            No blog posts yet. Create your first post!
          </Typography>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {blogs.map((blog) => (
            <Grid item xs={12} sm={6} md={4} key={blog.id}>
              <BlogCard
                blog={blog}
                onEdit={() => handleEditBlog(blog)}
                onDelete={() => handleDeleteBlog(blog.id)}
              />
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog open={openForm} onClose={() => setOpenForm(false)} maxWidth="md" fullWidth>
        <DialogTitle>{editingBlog ? 'Edit Blog Post' : 'Create New Blog Post'}</DialogTitle>
        <DialogContent>
          <BlogForm
            initialData={editingBlog || undefined}
            onSubmit={handleFormSubmit}
            onCancel={() => setOpenForm(false)}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default BlogsPage;
