import React, { useState, useEffect } from 'react';
import {
  TextField,
  Button,
  Box,
  CircularProgress,
  Typography,
} from '@mui/material';
import { Image as ImageIcon } from '@mui/icons-material';
import { BlogFormData } from '../types';
import { isValidGoogleDocsUrl, isValidImageUrl } from '../utils/validation';

interface BlogFormProps {
  initialData?: BlogFormData;
  onSubmit: (data: BlogFormData) => Promise<void> | void;
  onCancel: () => void;
  loading?: boolean;
}

const BlogForm: React.FC<BlogFormProps> = ({
  initialData,
  onSubmit,
  onCancel,
  loading = false,
}) => {
  const [formData, setFormData] = useState<BlogFormData>({
    title: '',
    googleDriveLink: '',
    image: '',
  });
  const [imagePreview, setImagePreview] = useState<string>('');
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Initialize form with initialData if provided
  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
      if (initialData.image) {
        setImagePreview(initialData.image);
      }
    }
  }, [initialData]);

  // Handle image preview when image URL changes
  useEffect(() => {
    if (formData.image) {
      // Create a proxy URL to handle CORS issues
      const proxyUrl = `https://images.weserv.nl/?url=${encodeURIComponent(formData.image)}&w=800`;

      // Try to load the image through the proxy
      const img = new Image();
      img.onload = () => {
        // If image loads successfully, use the original URL
        setImagePreview(formData.image);
      };
      img.onerror = () => {
        // If direct load fails, try the proxy URL
        setImagePreview(proxyUrl);
      };
      img.src = formData.image;
    } else {
      setImagePreview('');
    }
  }, [formData.image]);

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Title is required';
    }

    if (!formData.googleDriveLink) {
      newErrors.googleDriveLink = 'Google Drive link is required';
    } else if (!isValidGoogleDocsUrl(formData.googleDriveLink)) {
      newErrors.googleDriveLink = 'Please enter a valid Google Docs URL';
    }

    // Image is optional, but if provided, it should be a valid URL
    if (formData.image && !isValidImageUrl(formData.image)) {
      newErrors.image = 'Please enter a valid image URL';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for the field being edited
    if (errors[name as keyof typeof errors]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name as keyof typeof newErrors];
        return newErrors;
      });
    }
  };

  const handleImageUrlChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const url = e.target.value.trim();
    setFormData(prev => ({
      ...prev,
      image: url
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSubmit(formData);
    } catch (error) {
      console.error('Error submitting form:', error);
      setErrors(prev => ({ ...prev, form: 'Failed to submit form. Please try again.' }));
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
      <TextField
        margin="normal"
        required
        fullWidth
        id="title"
        label="Blog Title"
        name="title"
        autoComplete="off"
        autoFocus
        value={formData.title}
        onChange={handleChange}
        error={!!errors.title}
        helperText={errors.title}
        disabled={loading}
      />

      <TextField
        margin="normal"
        required
        fullWidth
        name="googleDriveLink"
        label="Google Drive Document Link"
        type="url"
        id="googleDriveLink"
        autoComplete="off"
        value={formData.googleDriveLink}
        onChange={handleChange}
        error={!!errors.googleDriveLink}
        helperText={
          errors.googleDriveLink ||
          'Make sure the document is shared with "Anyone with the link"'
        }
        disabled={loading}
      />

      <Box mt={2} mb={2}>
        <Typography variant="subtitle2" gutterBottom>
          Image URL (Optional)
        </Typography>
        <Box mb={3}>
          <TextField
            fullWidth
            label="Image URL"
            variant="outlined"
            name="image"
            value={formData.image}
            onChange={handleImageUrlChange}
            error={!!errors.image}
            helperText={errors.image || 'Paste the URL of your image (e.g., from Google Drive, Imgur, etc.)'}
            margin="normal"
            disabled={loading}
            InputProps={{
              startAdornment: <ImageIcon color="action" sx={{ mr: 1, color: 'text.secondary' }} />,
            }}
          />
          {imagePreview && (
            <Box mt={2} textAlign="center">
              <Typography variant="caption" display="block" gutterBottom>
                Image Preview:
              </Typography>
              <Box 
                sx={{
                  display: 'inline-block',
                  maxWidth: '100%',
                  maxHeight: '300px',
                  border: '1px solid #eee',
                  borderRadius: '4px',
                  overflow: 'hidden',
                  p: 1,
                  backgroundColor: '#f9f9f9',
                  position: 'relative'
                }}
              >
                <img
                  src={imagePreview}
                  alt="Preview"
                  style={{
                    maxWidth: '100%',
                    maxHeight: '280px',
                    objectFit: 'contain',
                    display: 'block',
                  }}
                  onError={(e) => {
                    // If we're already using the proxy URL and it fails, show error
                    if (imagePreview.includes('images.weserv.nl')) {
                      setImagePreview('');
                      setFormData(prev => ({ ...prev, image: '' }));
                      setErrors(prev => ({ 
                        ...prev, 
                        image: 'Failed to load image. Please ensure the URL is publicly accessible.' 
                      }));
                    } else {
                      // Try with proxy URL
                      const proxyUrl = `https://images.weserv.nl/?url=${encodeURIComponent(formData.image)}&w=800`;
                      setImagePreview(proxyUrl);
                    }
                  }}
                />
                <Box 
                  sx={{
                    position: 'absolute',
                    top: 8,
                    right: 8,
                    bgcolor: 'rgba(0,0,0,0.5)',
                    borderRadius: '50%',
                    width: 24,
                    height: 24,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    '&:hover': {
                      bgcolor: 'rgba(0,0,0,0.7)',
                    }
                  }}
                  onClick={(e) => {
                    e.stopPropagation();
                    setImagePreview('');
                    setFormData(prev => ({ ...prev, image: '' }));
                  }}
                >
                  <Typography color="white" variant="body2" sx={{ lineHeight: 1 }}>×</Typography>
                </Box>
              </Box>
            </Box>
          )}
        </Box>
      </Box>

      <Box mt={3} display="flex" justifyContent="flex-end" gap={2}>
        <Button
          onClick={onCancel}
          variant="outlined"
          disabled={loading}
        >
          Cancel
        </Button>
        <Button
          type="submit"
          variant="contained"
          color="primary"
          disabled={loading}
          startIcon={loading ? <CircularProgress size={20} /> : null}
        >
          {initialData ? 'Update Post' : 'Create Post'}
        </Button>
      </Box>
    </Box>
  );
};

export default BlogForm;
