import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  useTheme,
  Box,
  IconButton
} from '@mui/material';
import { 
  Edit as EditIcon, 
  Delete as DeleteIcon, 
  Image as ImageIcon, 
  AccessTime as AccessTimeIcon,
  Update as UpdateIcon,
  ArrowForward as ArrowForwardIcon
} from '@mui/icons-material';
import { Tooltip } from '@mui/material';
import { Blog } from '../types';
import BlogDetail from './BlogDetail';

interface BlogImage {
  data?: string;
  contentType?: string;
  filename?: string;
}

interface BlogCardProps {
  blog: Blog & { 
    _id?: string;
  };
  onEdit: () => void;
  onDelete: () => Promise<void>;
}

const BlogCard: React.FC<BlogCardProps> = ({ blog, onEdit, onDelete }) => {
  const [openDetail, setOpenDetail] = useState(false);
  const theme = useTheme();
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Handle image source extraction
  React.useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      return;
    }

    if (typeof blog.image === 'string') {
      // Handle data URLs or external URLs
      if (blog.image.startsWith('data:image/')) {
        setImageSrc(blog.image);
      } else if (blog.image.startsWith('http')) {
        setImageSrc(blog.image);
      } else {
        // Handle base64 string without data URL prefix
        setImageSrc(`data:image/jpeg;base64,${blog.image}`);
      }
    } else if (blog.image && typeof blog.image === 'object' && 'data' in blog.image) {
      // Handle image object with data and contentType
      const base64Data = blog.image.data;
      const mimeType = blog.image.contentType || 'image/jpeg';

      if (typeof base64Data === 'string' && base64Data.startsWith('data:image/')) {
        setImageSrc(base64Data);
      } else {
        setImageSrc(`data:${mimeType};base64,${base64Data}`);
      }
    } else {
      setImageSrc('');
    }

    // Reset error state when image changes
    setImageError(false);
  }, [blog.image]);

  const formatDate = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  const handleDeleteClick = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await onDelete();
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  return (
    <>
      <Card sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        transition: 'all 0.3s ease-in-out',
        borderRadius: '12px',
        overflow: 'hidden',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)',
        border: '1px solid rgba(0, 0, 0, 0.08)',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',
          borderColor: theme.palette.primary.light,
        },
      }}>
        {/* Blog Image */}
        <Box 
          onClick={() => setOpenDetail(true)}
          sx={{
            position: 'relative',
            width: '100%',
            paddingTop: '100%', // 1:1 aspect ratio
            cursor: 'pointer',
            overflow: 'hidden',
            backgroundColor: theme.palette.grey[50],
            '&:hover img': {
              transform: 'scale(1.05)',
              opacity: 0.95,
            },
            '&:hover::after': {
              content: '""',
              position: 'absolute',
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              background: 'rgba(0,0,0,0.05)',
              transition: 'all 0.3s ease',
            }
          }}
        >
          {imageSrc && !imageError ? (
            <Box
              component="img"
              src={imageSrc}
              alt={blog.title}
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                transition: 'transform 0.5s ease',
                backgroundColor: theme.palette.grey[100],
              }}
              onError={() => setImageError(true)}
            />
          ) : (
            <Box
              sx={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                backgroundColor: theme.palette.grey[100],
              }}
            >
              <ImageIcon sx={{ 
                fontSize: 64, 
                color: 'grey.400',
                opacity: 0.7,
              }} />
            </Box>
          )}
        </Box>

        {/* Blog Content */}
        <CardContent 
          sx={{ 
            flexGrow: 1, 
            cursor: 'pointer',
            p: 3,
            '&:last-child': {
              pb: 3,
            },
            display: 'flex',
            flexDirection: 'column',
            height: '100%',
          }} 
          onClick={() => setOpenDetail(true)}
        >
          <Box sx={{ flexGrow: 1 }}>
            <Typography 
              gutterBottom 
              variant="h6" 
              component="h2"
              sx={{
                fontWeight: 600,
                mb: 1.5,
                color: theme.palette.text.primary,
                display: '-webkit-box',
                WebkitLineClamp: 2,
                WebkitBoxOrient: 'vertical',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                minHeight: '3em',
                lineHeight: 1.3,
                letterSpacing: '-0.1px',
                fontSize: '1.1rem',
                '&:hover': {
                  color: theme.palette.primary.main,
                },
                [theme.breakpoints.down('sm')]: {
                  fontSize: '1.1rem',
                  minHeight: '3em',
                },
              }}
            >
              {blog.title}
            </Typography>
          </Box>

          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            mt: 'auto',
            pt: 2,
            color: theme.palette.text.secondary,
            flexWrap: 'wrap',
            gap: 1,
          }}>
            <Box sx={{ 
              display: 'flex', 
              alignItems: 'center',
              backgroundColor: theme.palette.grey[50],
              px: 2,
              py: 0.8,
              borderRadius: '20px',
              border: `1px solid ${theme.palette.grey[200]}`,
              transition: 'all 0.2s ease',
              '&:hover': {
                backgroundColor: theme.palette.grey[100],
              }
            }}>
              <AccessTimeIcon sx={{ 
                fontSize: '1rem', 
                mr: 0.8,
                color: theme.palette.primary.main,
                opacity: 0.9,
              }} />
              <Typography 
                variant="caption" 
                sx={{
                  fontSize: '0.75rem',
                  fontWeight: 500,
                  letterSpacing: '0.3px',
                  color: theme.palette.text.secondary,
                }}
              >
                {formatDate(blog.createdAt)}
              </Typography>
            </Box>
            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <Box 
                component="span"
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  backgroundColor: theme.palette.grey[50],
                  px: 2,
                  py: 0.8,
                  borderRadius: '20px',
                  border: `1px solid ${theme.palette.grey[200]}`,
                  transition: 'all 0.2s ease',
                  '&:hover': {
                    backgroundColor: theme.palette.grey[100],
                  }
                }}
              >
                <UpdateIcon sx={{ 
                  fontSize: '1rem', 
                  mr: 0.8,
                  color: theme.palette.success.main,
                  opacity: 0.9,
                }} />
                <Typography 
                  variant="caption" 
                  sx={{
                    fontSize: '0.75rem',
                    fontWeight: 500,
                    color: theme.palette.text.secondary,
                  }}
                >
                  Updated
                </Typography>
              </Box>
            )}
          </Box>
        </CardContent>

        {/* Actions */}
        <CardActions sx={{ 
          justifyContent: 'space-between',
          p: theme.spacing(0, 2, 2, 2),
          mt: 'auto',
          '& > *': {
            margin: theme.spacing(0.25),
          },
          '& .MuiButton-root': {
            minWidth: '32px',
            padding: '4px 8px',
            fontSize: '0.75rem',
          },
          '& .MuiSvgIcon-root': {
            fontSize: '1rem',
          },
          borderTop: `1px solid ${theme.palette.divider}`,
        }}>
          <Button 
            variant="outlined"
            size="medium" 
            color="primary"
            onClick={(e) => {
              e.stopPropagation();
              setOpenDetail(true);
            }}
            sx={{
              textTransform: 'none',
              fontWeight: 500,
              borderRadius: '10px',
              px: 2.5,
              py: 0.8,
              fontSize: '0.85rem',
              borderWidth: '1.5px',
              '&:hover': {
                borderWidth: '1.5px',
                backgroundColor: theme.palette.primary.light + '10',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.08)',
                transform: 'translateY(-1px)',
              },
              transition: 'all 0.2s ease',
            }}
            endIcon={<ArrowForwardIcon fontSize="small" sx={{ ml: 0.5 }} />}
          >
            Read More
          </Button>

          <Box sx={{ 
            display: 'flex', 
            gap: 0.8,
            '& .MuiIconButton-root': {
              width: 36,
              height: 36,
              transition: 'all 0.2s ease',
              '&:hover': {
                transform: 'translateY(-1px)',
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
              }
            }
          }}>
            <Tooltip title="Edit">
              <IconButton 
                size="small" 
                onClick={(e) => {
                  e.stopPropagation();
                  onEdit();
                }}
                sx={{
                  color: theme.palette.primary.main,
                  backgroundColor: theme.palette.primary.light + '15',
                  '&:hover': {
                    backgroundColor: theme.palette.primary.light + '30',
                  },
                  transition: 'all 0.2s ease',
                }}
              >
                <EditIcon fontSize="small" />
              </IconButton>
            </Tooltip>

            <Tooltip title="Delete">
              <IconButton 
                size="small" 
                onClick={async (e) => {
                  e.stopPropagation();
                  await handleDeleteClick();
                }}
                sx={{
                  color: theme.palette.error.main,
                  backgroundColor: theme.palette.error.light + '15',
                  '&:hover': {
                    backgroundColor: theme.palette.error.light + '30',
                  },
                  transition: 'all 0.2s ease',
                }}
              >
                <DeleteIcon fontSize="small" />
              </IconButton>
            </Tooltip>
          </Box>
        </CardActions>
      </Card>

      {/* Blog Detail Dialog */}
      <BlogDetail
        blog={blog}
        open={openDetail}
        onClose={() => setOpenDetail(false)}
        onEdit={onEdit}
        onDelete={handleDeleteClick}
      />
    </>
  );
};

export default BlogCard;
