import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import {
  Button,
  Typography,
  CircularProgress,
  Box,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  Snackbar,
  Alert,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import BlogForm from '../components/BlogForm';
import BlogCard from '../components/BlogCard';
import { Blog, BlogFormData } from '../types';
import { useAuth } from '../contexts/AuthContext';

// This type is used for the API response where we get _id from MongoDB
interface BlogResponse {
  _id: string;
  title: string;
  googleDriveLink: string;
  image?: {
    data: string; // base64 encoded image data
    contentType: string;
    filename?: string;
  } | string; // Support both new and old format during transition
  createdAt: string;
  updatedAt?: string;
  author?: {
    id: string;
    name: string;
    email: string;
  };
}

const BlogsPage: React.FC = () => {
  const [blogs, setBlogs] = useState<Blog[]>([]);
  const [loading, setLoading] = useState(true);
  const [openForm, setOpenForm] = useState(false);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState<'success' | 'error'>('success');

  const [editingBlog, setEditingBlog] = useState<BlogFormData | null>(null);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();
  const navigate = useNavigate();

  // Fetch blogs from API
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const response = await api.get('/api/blogs');
        // Convert API response to our Blog type
        const blogsWithId = response.data.map((blog: BlogResponse): Blog => {
          // Handle both string (legacy) and object (new) image formats
          let imageData = blog.image;

          // If image is an object with data, ensure it's a proper BlogImage type
          if (blog.image && typeof blog.image === 'object' && 'data' in blog.image) {
            imageData = {
              data: blog.image.data,
              contentType: blog.image.contentType || 'image/jpeg',
              filename: blog.image.filename || 'image.jpg'
            };
          }

          return {
            id: blog._id, // Use _id as id
            title: blog.title,
            googleDriveLink: blog.googleDriveLink,
            image: imageData,
            createdAt: blog.createdAt,
            updatedAt: blog.updatedAt,
            author: blog.author
          };
        });

        setBlogs(blogsWithId);
      } catch (error) {
        console.error('Error fetching blogs:', error);
        setError('Failed to load blogs. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  const handleCreateBlog = () => {
    if (!user) {
      // Store the current location to redirect back after login
      sessionStorage.setItem('redirectAfterLogin', window.location.pathname);
      navigate('/login');
      return;
    }
    setEditingBlog(null);
    setOpenForm(true);
  };

  const handleEditBlog = (blog: Blog) => {
    if (!user) {
      sessionStorage.setItem('redirectAfterLogin', window.location.pathname);
      navigate('/login');
      return;
    }

    // Convert Blog to BlogFormData
    const blogToEdit: BlogFormData = {
      title: blog.title,
      googleDriveLink: blog.googleDriveLink,
      image: ''
    };

    // Handle image data
    if (blog.image) {
      if (typeof blog.image === 'string') {
        blogToEdit.image = blog.image;
      } else if (blog.image.data) {
        // Convert image data to data URL
        blogToEdit.image = `data:${blog.image.contentType || 'image/jpeg'};base64,${blog.image.data}`;
      }
    }

    setEditingBlog(blogToEdit);
    setOpenForm(true);
  };

  const handleDeleteBlog = async (id: string) => {
    if (!user) {
      sessionStorage.setItem('redirectAfterLogin', window.location.pathname);
      navigate('/login');
      return;
    }

    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await api.delete(`/api/blogs/${id}`);
        setBlogs(blogs.filter(blog => blog.id !== id));
      } catch (error: any) {
        console.error('Error deleting blog:', error);
        setError(error.response?.data?.message || 'Failed to delete blog');
      }
    }
  };

  const handleCloseForm = () => {
    setOpenForm(false);
    setEditingBlog(null);
  };

  const showSnackbar = (message: string, severity: 'success' | 'error' = 'success') => {
    setSnackbarMessage(message);
    setSnackbarSeverity(severity);
    setSnackbarOpen(true);
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  const handleFormSubmit = async (formData: BlogFormData) => {
    try {
      if (editingBlog) {
        // Update existing blog
        const response = await api.put(`/api/blogs/${editingBlog.id}`, formData);
        setBlogs(blogs.map(blog => 
          blog.id === editingBlog.id ? { 
            ...blog, 
            ...response.data,
            id: response.data._id || blog.id 
          } : blog
        ));
        showSnackbar('Blog updated successfully');
      } else {
        // Create new blog
        const response = await api.post('/api/blogs', formData);
        setBlogs([{ 
          ...response.data, 
          id: response.data._id,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString()
        }, ...blogs]);
        showSnackbar('Blog created successfully');
      }
      setOpenForm(false);
      setEditingBlog(null);
    } catch (error: any) {
      console.error('Error saving blog:', error);
      const errorMessage = error.response?.data?.message || 'Failed to save blog. Please try again.';
      setError(errorMessage);
      showSnackbar(errorMessage, 'error');
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <div>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={4}>
        <Typography variant="h4" component="h1" gutterBottom>
          Blog Posts
        </Typography>
        {user && (
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={handleCreateBlog}
          >
            Create Blog Post
          </Button>
        )}
      </Box>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
      >
        <Alert 
          onClose={handleCloseSnackbar} 
          severity={snackbarSeverity} 
          sx={{ width: '100%' }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>

      {error && (
        <Snackbar
          open={!!error}
          autoHideDuration={6000}
          onClose={() => setError(null)}
        >
          <Alert onClose={() => setError(null)} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>
      )}

      {blogs.length === 0 ? (
        <Box textAlign="center" py={4}>
          <Typography variant="h6" color="textSecondary">
            No blog posts yet. Create your first post!
          </Typography>
        </Box>
      ) : (
        <Grid container spacing={3}>
          {blogs.map((blog) => (
            <Grid item xs={12} sm={6} md={4} key={blog.id}>
              <BlogCard
                blog={blog}
                onEdit={() => handleEditBlog(blog)}
                onDelete={() => handleDeleteBlog(blog.id)}
              />
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog open={openForm} onClose={() => setOpenForm(false)} maxWidth="md" fullWidth>
        <DialogTitle>{editingBlog ? 'Edit Blog Post' : 'Create New Blog Post'}</DialogTitle>
        <DialogContent>
          <BlogForm
            initialData={editingBlog || undefined}
            onSubmit={handleFormSubmit}
            onCancel={() => setOpenForm(false)}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default BlogsPage;
