import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Divider,
  IconButton,
  Typography,
  Box,
  useMediaQuery,
  useTheme,
  Button,
  CircularProgress,
  Avatar,
  Paper,
  Fade,
  Zoom,
} from '@mui/material';
import { 
  Close as CloseIcon, 
  OpenInNew as OpenInNewIcon, 
  Image as ImageIcon,
  Edit as EditIcon,
  Delete as DeleteIcon,
  CalendarToday as CalendarIcon,
  Update as UpdateIcon,
  Person as PersonIcon
} from '@mui/icons-material';
import { format } from 'date-fns';
import { Blog } from '../types';

interface BlogDetailProps {
  blog: Blog;
  open: boolean;
  onClose: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

const BlogDetail: React.FC<BlogDetailProps> = ({ blog, open, onClose, onEdit, onDelete }) => {
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));
  const [loading, setLoading] = useState(true);
  const [imageSrc, setImageSrc] = useState<string>('');
  const [imageError, setImageError] = useState(false);

  // Format date for display
  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMMM d, yyyy');
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  // Handle image source extraction
  useEffect(() => {
    if (!blog.image) {
      setImageSrc('');
      setLoading(false);
      return;
    }

    if (typeof blog.image === 'string') {
      // Handle data URLs or external URLs
      if (blog.image.startsWith('data:image/') || blog.image.startsWith('http')) {
        setImageSrc(blog.image);
        setLoading(false);
      } else {
        // Handle base64 string without data URL prefix
        setImageSrc(`data:image/jpeg;base64,${blog.image}`);
        setLoading(false);
      }
    } else if (blog.image && 'data' in blog.image) {
      // Handle image object with data and contentType
      const base64Data = blog.image.data;
      const mimeType = blog.image.contentType || 'image/jpeg';

      if (typeof base64Data === 'string' && base64Data.startsWith('data:image/')) {
        setImageSrc(base64Data);
      } else {
        setImageSrc(`data:${mimeType};base64,${base64Data}`);
      }
      setLoading(false);
    } else {
      setImageSrc('');
      setLoading(false);
    }

    // Reset error state when image changes
    setImageError(false);
  }, [blog.image]);

  // Extract Google Docs ID from URL
  const getGoogleDocsEmbedUrl = (url: string) => {
    if (!url) return '';
    try {
      // Extract document ID using a more robust method
      const match = url.match(/[\w-]{25,}/);
      if (match && match[0]) {
        const docId = match[0];
        return `https://docs.google.com/document/d/${docId}/preview?rm=minimal&embedded=true&single=true&widget=false&headers=false&chrome=false`;
      }
      return url;
    } catch (e) {
      console.error('Error parsing Google Docs URL:', e);
      return url;
    }
  };

  // Handle iframe load
  const handleIframeLoad = () => {
    setLoading(false);
    // Force a small delay to ensure the iframe is fully rendered
    setTimeout(() => {
      const iframe = document.querySelector('iframe');
      if (iframe) {
        iframe.style.opacity = '1';
        iframe.style.transition = 'opacity 0.3s ease-in-out';
      }
    }, 100);
  };

  return (
    <Dialog
      fullScreen={fullScreen}
      open={open}
      onClose={onClose}
      maxWidth="xl"
      fullWidth
      PaperProps={{
        sx: {
          height: '95vh',
          maxHeight: 'none',
          borderRadius: 3,
          overflow: 'hidden',
          background: theme.palette.background.default,
          boxShadow: theme.shadows[10],
          '& .MuiDialog-container': {
            backdropFilter: 'blur(4px)',
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            padding: fullScreen ? 0 : 2,
          },
          [theme.breakpoints.up('md')]: {
            height: '90vh',
            maxHeight: '1200px',
          }
        },
      }}
      transitionDuration={300}
      TransitionComponent={Fade}
    >
      <DialogTitle sx={{ 
        background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.primary.dark} 100%)`,
        color: theme.palette.primary.contrastText,
        py: 2,
        px: 3,
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        boxShadow: theme.shadows[2],
        position: 'relative',
        '&::after': {
          content: '""',
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          height: '1px',
          background: `linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent)`,
        }
      }}>
        <Typography 
          variant="h5" 
          component="h1" 
          noWrap 
          sx={{ 
            flex: 1, 
            mr: 2,
            fontWeight: 700,
            letterSpacing: '-0.3px',
            textShadow: '0 1px 2px rgba(0,0,0,0.1)'
          }}
        >
          {blog.title}
        </Typography>
        <IconButton 
          edge="end" 
          onClick={onClose} 
          sx={{
            color: 'rgba(255,255,255,0.9)',
            backgroundColor: 'rgba(255,255,255,0.1)',
            '&:hover': {
              backgroundColor: 'rgba(255,255,255,0.2)',
            },
            transition: 'all 0.2s ease',
          }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent dividers sx={{ p: 0, display: 'flex', flexDirection: 'column', flex: 1, overflow: 'hidden' }}>
        {/* Blog Metadata */}
        <Paper 
          elevation={0} 
          sx={{ 
            p: 2.5, 
            backgroundColor: theme.palette.background.paper,
            borderBottom: `1px solid ${theme.palette.divider}`,
            borderRadius: 0,
          }}
        >
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: 1.5,
            '& .MuiDivider-root': {
              height: 16,
              alignSelf: 'center',
              backgroundColor: theme.palette.divider,
            }
          }}>
            {blog.author && (
              <Zoom in={true} style={{ transitionDelay: '100ms' }}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar 
                    sx={{ 
                      width: 36, 
                      height: 36, 
                      mr: 1.5,
                      backgroundColor: theme.palette.primary.light,
                      color: theme.palette.primary.contrastText,
                      fontSize: '0.9rem',
                      boxShadow: theme.shadows[1],
                      '& .MuiSvgIcon-root': {
                        fontSize: '1.1rem'
                      }
                    }}
                  >
                    {blog.author.name ? (
                      blog.author.name.split(' ').map(n => n[0]).join('').toUpperCase()
                    ) : (
                      <PersonIcon />
                    )}
                  </Avatar>
                  <Box>
                    <Typography variant="subtitle2" color="text.primary" fontWeight={500}>
                      {blog.author.name || 'Unknown Author'}
                    </Typography>
                  </Box>
                </Box>
              </Zoom>
            )}

            <Divider orientation="vertical" flexItem />

            <Zoom in={true} style={{ transitionDelay: '150ms' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', color: theme.palette.text.secondary }}>
                <CalendarIcon sx={{ fontSize: '1rem', mr: 0.8, opacity: 0.8 }} />
                <Typography variant="caption" sx={{ fontSize: '0.8rem' }}>
                  {formatDate(blog.createdAt)}
                </Typography>
              </Box>
            </Zoom>

            {blog.updatedAt && blog.updatedAt !== blog.createdAt && (
              <>
                <Divider orientation="vertical" flexItem />
                <Zoom in={true} style={{ transitionDelay: '200ms' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', color: theme.palette.text.secondary }}>
                    <UpdateIcon sx={{ fontSize: '1rem', mr: 0.8, opacity: 0.8 }} />
                    <Typography variant="caption" sx={{ fontSize: '0.8rem' }}>
                      Updated: {formatDate(blog.updatedAt)}
                    </Typography>
                  </Box>
                </Zoom>
              </>
            )}
          </Box>
        </Paper>

        {/* Blog Image */}
        <Box sx={{ 
          position: 'relative',
          width: '100%',
          maxHeight: 350,
          overflow: 'hidden',
          backgroundColor: theme.palette.grey[50],
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          borderBottom: `1px solid ${theme.palette.divider}`,
          transition: 'all 0.3s ease',
          '&:hover img': {
            transform: 'scale(1.02)',
          },
          '&::after': {
            content: '""',
            position: 'absolute',
            bottom: 0,
            left: 0,
            right: 0,
            height: '60px',
            background: 'linear-gradient(to top, rgba(0,0,0,0.1), transparent)'
          }
        }}>
          {loading ? (
            <Box sx={{ 
              display: 'flex', 
              justifyContent: 'center', 
              alignItems: 'center', 
              width: '100%',
              height: 300,
              backgroundColor: theme.palette.grey[50]
            }}>
              <CircularProgress size={32} thickness={4} />
            </Box>
          ) : imageError || !imageSrc ? (
            <Box sx={{ 
              width: '100%',
              height: 250,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              color: theme.palette.text.disabled,
              p: 3,
              textAlign: 'center',
            }}>
              <ImageIcon sx={{ fontSize: 64, mb: 1.5, opacity: 0.5 }} />
              <Typography variant="body2" sx={{ opacity: 0.7 }}>
                {imageError ? 'Failed to load image' : 'No image available'}
              </Typography>
            </Box>
          ) : (
            <Zoom in={!loading} style={{ transitionDelay: '250ms' }}>
              <Box sx={{ 
                width: '100%',
                height: '100%',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
                position: 'relative',
              }}>
                <img
                  src={imageSrc}
                  alt={blog.title}
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    objectPosition: 'center',
                    transition: 'transform 0.5s ease',
                    maxHeight: '350px',
                  }}
                  onLoad={() => setLoading(false)}
                  onError={() => setImageError(true)}
                />
              </Box>
            </Zoom>
          )}
        </Box>

        {/* Blog Content */}
        <Box sx={{ 
          flex: 1, 
          position: 'relative', 
          overflow: 'auto',
          display: 'flex',
          flexDirection: 'column',
          backgroundColor: theme.palette.grey[100],
          p: 0,
          m: 0,
          '& iframe': {
            border: 'none',
            width: '100%',
            height: '100%',
            minHeight: 'calc(100vh - 300px)',
            backgroundColor: theme.palette.background.paper,
            '&.MuiBox-root': {
              borderRadius: 0,
              p: 0,
              m: 0,
            },
            '& body': {
              backgroundColor: 'transparent !important',
              padding: '20px 0 !important',
              '& .kix-page-paginated': {
                boxShadow: 'none !important',
                backgroundColor: 'transparent !important',
              },
              '& .kix-page': {
                backgroundColor: 'transparent !important',
                boxShadow: 'none !important',
              },
              '& .kix-page-content': {
                padding: '40px 60px !important',
                maxWidth: '900px',
                margin: '0 auto !important',
                backgroundColor: `${theme.palette.background.paper} !important`,
                boxShadow: theme.shadows[1],
              }
            }
          }
        }}>
          {!loading && (
            <Box
              component="iframe"
              src={getGoogleDocsEmbedUrl(blog.googleDriveLink)}
              title={blog.title}
              width="100%"
              height="100%"
              frameBorder="0"
              onLoad={handleIframeLoad}
              style={{
                flex: 1,
                minHeight: 'calc(100vh - 300px)',
                opacity: 0,
                transition: 'opacity 0.3s ease-in-out',
                border: 'none',
                backgroundColor: theme.palette.background.paper,
              }}
              allowFullScreen
            />
          )}
        </Box>
      </DialogContent>

      <DialogActions sx={{ 
        px: 3, 
        py: 2, 
        borderTop: `1px solid ${theme.palette.divider}`,
        justifyContent: 'space-between',
        backgroundColor: theme.palette.background.paper,
        '&.MuiDialogActions-root': {
          padding: theme.spacing(2, 3),
        }
      }}>
        <Box sx={{ display: 'flex', gap: 1.5 }}>
          {onEdit && (
            <Button 
              onClick={onEdit} 
              variant="outlined"
              color="primary"
              size="medium"
              startIcon={<EditIcon />}
              sx={{
                textTransform: 'none',
                borderRadius: '8px',
                px: 2,
                py: 0.8,
                fontWeight: 500,
                transition: 'all 0.2s ease',
                '&:hover': {
                  backgroundColor: theme.palette.primary.light + '10',
                  transform: 'translateY(-1px)',
                }
              }}
            >
              Edit Post
            </Button>
          )}
          {onDelete && (
            <Button 
              onClick={onDelete} 
              variant="outlined"
              color="error"
              size="medium"
              startIcon={<DeleteIcon />}
              sx={{
                textTransform: 'none',
                borderRadius: '8px',
                px: 2,
                py: 0.8,
                fontWeight: 500,
                transition: 'all 0.2s ease',
                '&:hover': {
                  backgroundColor: theme.palette.error.light + '10',
                  transform: 'translateY(-1px)',
                }
              }}
            >
              Delete
            </Button>
          )}
        </Box>
        <Button 
          variant="contained" 
          color="primary" 
          endIcon={<OpenInNewIcon />}
          href={blog.googleDriveLink} 
          target="_blank"
          rel="noopener noreferrer"
          size="medium"
          sx={{
            textTransform: 'none',
            borderRadius: '8px',
            px: 2.5,
            py: 0.9,
            fontWeight: 500,
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            transition: 'all 0.2s ease',
            '&:hover': {
              transform: 'translateY(-1px)',
              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
            }
          }}
        >
          Open in Google Docs
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default BlogDetail;
