import React, { useEffect, useCallback } from 'react';
import { 
  Box, 
  Typography, 
  useTheme, 
  useMediaQuery, 
  IconButton, 
  Button,
  Card,
  CardMedia
} from '@mui/material';
import { KeyboardArrowLeft, KeyboardArrowRight, ArrowForward } from '@mui/icons-material';
import { Blog } from '../types';

interface BlogCarouselProps {
  blogs: (Blog & { _id?: string })[];
  onEdit: (blog: Blog & { _id?: string }) => void;
  onDelete: (blogId: string) => Promise<void> | void;
}

const BlogCarousel: React.FC<BlogCarouselProps> = ({ blogs, onEdit, onDelete }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  const [currentIndex, setCurrentIndex] = React.useState(0);
  const [isPaused, setIsPaused] = React.useState(false);
  const itemsPerPage = isMobile ? 1 : 3;
  const totalItems = blogs.length;
  const totalSlides = Math.ceil(totalItems / itemsPerPage);

  // Auto-advance effect
  useEffect(() => {
    if (totalItems <= itemsPerPage) return;

    const interval = setInterval(() => {
      if (!isPaused) {
        setCurrentIndex(prevIndex => 
          prevIndex >= totalItems - itemsPerPage ? 0 : prevIndex + 1
        );
      }
    }, 3000);

    return () => clearInterval(interval);
  }, [totalItems, itemsPerPage, isPaused]);

  const goToNext = useCallback(() => {
    setCurrentIndex(prevIndex => 
      prevIndex >= totalItems - itemsPerPage ? 0 : prevIndex + 1
    );
  }, [totalItems, itemsPerPage]);

  const goToPrev = useCallback(() => {
    setCurrentIndex(prevIndex =>
      prevIndex === 0 ? totalItems - itemsPerPage : prevIndex - 1
    );
  }, [totalItems, itemsPerPage]);

  // Pause auto-advance when user interacts
  const handleMouseEnter = () => setIsPaused(true);
  const handleMouseLeave = () => setIsPaused(false);



  const handleNext = () => {
    setCurrentIndex((prevIndex) => 
      prevIndex >= blogs.length - itemsPerPage ? 0 : prevIndex + 1
    );
  };

  const handlePrevious = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? blogs.length - itemsPerPage : prevIndex - 1
    );
  };

  if (!blogs.length) {
    return (
      <Box textAlign="center" py={4}>
        <Typography variant="h6" color="textSecondary">
          No blog posts available
        </Typography>
      </Box>
    );
  }

  return (
    <Box 
      sx={{ position: 'relative', width: '100%', overflow: 'hidden' }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Box 
        sx={{
          display: 'flex',
          transition: 'transform 0.5s ease-in-out',
          transform: `translateX(-${currentIndex * (100 / itemsPerPage)}%)`,
          gap: 2,
          px: 2,
          '&:hover': {
            cursor: 'grab'
          },
          '&:active': {
            cursor: 'grabbing'
          }
        }}
      >
        {[...blogs, ...blogs.slice(0, itemsPerPage)].map((blog, index) => (
          <Box 
            key={blog._id || blog.title} 
            sx={{ 
              position: 'relative',
              minWidth: `calc(${100 / itemsPerPage}% - ${theme.spacing(2)})`,
              flexShrink: 0,
              px: 1,
              borderRadius: 1,
              overflow: 'hidden',
              height: 400, 
              '&:hover .carousel-overlay': {
                opacity: 1,
                transform: 'translateY(0)'
              }
            }}
          >
            <Card sx={{ height: '100%', position: 'relative' }}>
              <CardMedia
                component="img"
                height="100%"
                image={typeof blog.image === 'string' ? blog.image : blog.image?.data}
                alt={blog.title}
                sx={{
                  objectFit: 'cover',
                  width: '100%',
                  height: '100%',
                }}
              />
              <Box 
                className="carousel-overlay"
                sx={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  p: 2,
                  background: `linear-gradient(to top, ${theme.palette.common.black} 0%, transparent 100%)`,
                  color: theme.palette.common.white,
                  opacity: 0.9,
                  transition: 'all 0.3s ease',
                  transform: 'translateY(20px)'
                }}
              >
                <Typography variant="h6" noWrap sx={{ fontWeight: 'bold' }}>
                  {blog.title}
                </Typography>
                <Button 
                  size="small" 
                  color="primary" 
                  variant="contained"
                  endIcon={<ArrowForward />}
                  onClick={() => onEdit(blog)}
                  sx={{
                    mt: 1,
                    backgroundColor: theme.palette.primary.main,
                    '&:hover': {
                      backgroundColor: theme.palette.primary.dark,
                    }
                  }}
                >
                  See More
                </Button>
              </Box>
            </Card>
          </Box>
        ))}
      </Box>

      {blogs.length > itemsPerPage && (
        <>
          <IconButton
            onClick={goToPrev}
            onMouseDown={e => e.stopPropagation()}
            sx={{
              position: 'absolute',
              left: 0,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
              },
              zIndex: 1,
              boxShadow: 1,
            }}
          >
            <KeyboardArrowLeft />
          </IconButton>
          <IconButton
            onClick={goToNext}
            onMouseDown={e => e.stopPropagation()}
            sx={{
              position: 'absolute',
              right: 0,
              top: '50%',
              transform: 'translateY(-50%)',
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              '&:hover': {
                backgroundColor: 'rgba(255, 255, 255, 0.95)',
              },
              zIndex: 1,
              boxShadow: 1,
            }}
          >
            <KeyboardArrowRight />
          </IconButton>
        </>
      )}
    </Box>
  );
};

export default BlogCarousel;
